void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    if (col.x > 0.05) { // occasional wipe of plain color gradient
        col.y = 0.2 + 0.3 * sin(iTime * 3.14159265 * 0.1); // slow cyclical color change over time
        float z = col.x; // funny bar patterns
        for (int i = 0; i < int(z * 4.0); i++) { // repeat # of times depending on starting red channel value
            if (uv.y > 0.4) // top half of screen
                col.xyz = mod(col.yzx, mod(iTime, uv.y + 3.0));
            else // bottom half
                col.xyz = mod(col.xzy, mod(iTime, uv.x + 3.0));
        }
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}
