// -7 by Coyote

void mainImage(out vec4 O, vec2 U) {    
    O-=O;
    for(
        vec3 R = iResolution, 
             D = normalize( vec3( U+U, -3.5*R.y ) - R ),
             p = iTime*(2./R-2.)-.7,
             T = vec3(.4,.6,1),
             i = R-R, l;
        i.x < 2e2;
        T /= exp( max(0., texture(iChannel1,p/128.).a -.67) * vec3(1.5,3,6) )
    )
        l = .01* ++i / length( dot(R = fract(p+=D) - texture(iChannel0,p/32.).rgb,D)*D - R ),  
        O.rgb += T * min( l*l*l,1e3 )/i/i;
    O = sqrt(O); 
}






