float dist(in vec2 p,in vec2 q) {
    float difx = p.x-q.x;
    float dify = p.y-q.y;
    return sqrt(difx*difx+dify*dify);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 m = iMouse.xy/iResolution.xy;
    if (ivec2(iMouse.xy) == ivec2(0,0)) m = vec2(0.0);
    vec2 p = fragCoord/iResolution.xy;
    //vector array of points
    vec2 points[10] = 
    vec2[10](
    vec2(0.0,0.0), //1
    vec2(0.54,0.22), //2
    vec2(0.23,0.67), //3
    vec2(0.13,0.98), //4
    vec2(0.66,0.14), //5
    vec2(0.73,0.09), //6
    vec2(0.99,0.24), //7
    vec2(0.74,0.14), //8
    vec2(0.73,0.19), //9
    vec2(m.x,m.y)  //10 (mouse position vector)
    );

    //minimum distance variable
    float mindist;

    for (int i = 0;i<10;i++){
        if (i == 0) mindist = dist(p,points[i]);
        else {
            if ( mindist > dist(p,points[i]) ) mindist = dist(p,points[i]);
        }
    }
    //to make the color ramp more pleasant
    mindist = sqrt(mindist);
    fragColor = vec4(mindist,mindist,mindist,0.0);
}
