//operacion de Union  por FabriceNeyret2
#define opU2(d1, d2) ( d1.x < d2.x ? d1 : d2 )

float sdBox( vec3 p, vec3 b )
	{ vec3 d = abs(p) - b;   return length(max(d,0.0))+ min(max(d.x,max(d.y,d.z)),0.0); }
float sdCylinderYZ( vec3 p, vec2 h )
	{ vec2 d = abs(vec2(length(p.yz),p.x)) - h; return min(max(d.x,d.y),0.0) + length(max(d,0.0)); }
float sdCylinderXZ( vec3 p, vec2 h )
	{ vec2 d = abs(vec2(length(p.xz),p.y)) - h; return min(max(d.x,d.y),0.0) + length(max(d,0.0)); }
float sdCylinderXY( vec3 p, vec2 h )
	{ vec2 d = abs(vec2(length(p.xy),p.z)) - h; return min(max(d.x,d.y),0.0) + length(max(d,0.0)); }

float Intersect(float distA, float distB)
	{ return max(distA, distB);}
float Union(float distA, float distB)
	{ return min(distA, distB);}
float Difference(float distA, float distB)
	{ return max(distA, -distB);}

// object transformation
vec3 rotate_x(vec3 p, float phi)
{   float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 rotate_y(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}
vec3 rotate_z(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}
vec2 rotatev2(vec2 p, float ang)
{   float c = cos(ang); float s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}
float puertaMed(vec2 p, vec2 med)
{   float d1= sdBox( p,med );
    float d2= sdTriangleIsosceles(vec2(p.x, -p.y)-vec2(0.0,-med.y*2.0+0.5),med );
    float duni= Union(d1, d2);
    return duni;
}

vec2 puertaFinal(vec3 p, float distbox)
{   vec2 res= vec2(9999.0, -1.0);  vec3 p0=p;
    float d0=distbox;
    float d0a=sdBox(p-vec3(0.0,-3.0,0.0), vec3(4.0,7.0,11.0) );  //puerta box
    float d0b= puertaMed(p.xy-vec2(0.0,-6.0),  vec2(3.5,4.5)); //puerta A
    float d0c= puertaMed(p.xy-vec2(0.0,-6.0),  vec2(3.0,4.0)); //puerta B
     d0a= Intersect(d0a, d0b); //marco
     d0a= Difference(d0a, d0c);
     d0= Difference(d0, d0c);
    vec2 pra= rotatev2(p.yz, radians(90.0));

    float d2a= sdTunnel(pra-vec2(0.0,-0.5), vec2(3.0,6.0) );
    d0= Difference(d0, d2a);
    d0a= Difference(d0a, d2a);  ///puerta otra
    res =opU2(res, vec2(d0,105.0));
    res =opU2(res, vec2(d0a,106.0));
    return res;
}

vec2 GetDist(vec3 p  ) 
{	vec2 res= vec2(9999.0, -1.0);  
    p.xyz=p.zyx;
    vec3 p0=p;
	float planeDist1 = p.y-1.0;  //piso inf
    p.y=p.y-8.0;
    float da=sdBox(p-vec3(0.0,-11.0,0.0), vec3(20.0,1.0,20.0) ); //base
    float d0a=sdBox(p, vec3(10.0,10.0,10.0) );  //edificio a
    float d0b=sdBox(p-vec3(0.0,10.0,0.0), vec3(9.0,3.0,9.0) );  //edificio b
   d0a= Difference(d0a, d0b);
   vec2 pf1= puertaFinal(p-vec3(0.0,-0.5,0.0), d0a);

    p.x=abs(p.x)-13.0;
    p.z=abs(p.z)-11.0;
    float d1a= sdCylinderXZ( p-vec3(0.0,1.0,0.0), vec2(4.0,12.0) );
    float d1b= sdCylinderXZ( p-vec3(0.0,1.0,0.0), vec2(3.0,14.0) );
    d1a= Difference(d1a, d1b);

    res =opU2(res, vec2(da,105.0));
    res =opU2(res, pf1);
    res =opU2(res, vec2(d1a,105.0));
    return res;
}

vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

vec2 RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   vec3 p;
    vec2 hit, object=vec2(0.1,0.0);
    for(int i=0; i <= PMaxSteps; i++) 
    { p = ro + rd*object.x;
      hit = GetDist(p);
      object.x += hit.x;
      object.y = hit.y;
      if (abs(hit.x) < EPSILON || object.x > MAX_DIST) break;
    }    
    return object;
}

float getSoftShadow(vec3 p, vec3 lightPos) {
    float res = 9999.0;
    float dist = 0.01;
    float lightSize = 0.03;
    for (int i = 0; i < MAX_STEPS; i++) {
      float hit = GetDist(p + lightPos * dist).x;
      res = min(res, hit / (dist * lightSize));
      dist += hit;
      if (hit < 0.0001 || dist > 60.0) break;
    }
    return clamp(res, 0.0, 1.0);
}

float occlusion(vec3 pos, vec3 nor)
{   float sca = 2.0, occ = 0.0;
    for(int i = 0; i < 10; i++) {    
      float hr = 0.01 + float(i) * 0.5 / 4.0;
      float dd = GetDist(nor * hr + pos).x;
      occ += (hr - dd)*sca;
      sca *= 0.6;
    }
    return clamp( 1.0 - occ, 0.0, 1.0 );    
}

vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro,vec3 lightColor, float t) 
{   vec3 lightPos=lp;
    vec3 worldPos = p;
    vec3 V = -rd;
    vec3 N = normal;
    vec3 L = normalize (lightPos - worldPos);
    vec3 R = reflect (-L, N);

    float lightDist = max(length(L), .001);
    float atten=1.0 / (1.0 + lightDist * 0.125 + lightDist * lightDist * .05);
    L /= (lightDist*atten);

    float shadow = getSoftShadow(worldPos, L);// shadows

    float occ = occlusion(worldPos, N);// occ
    vec3 ambient = Ka + Ka * dot(normal, vec3(0., 1., 0.))*lightColor;
    ambient*=0.5;

    vec3 fresnel =  lightColor *  pow(clamp(1.0 + dot(rd, N), 0.0, 1.0), 2.0);;
    float diff= clamp(dot(N, L), 0.0, 1.0);
    vec3 diffuse =  lightColor * diff;
    float shininess=10.0;
    float specular    = pow(max(dot(R, V), 0.0), shininess);

    vec3 back = 0.5 * lightColor * clamp(dot(N, -L), 0.0, 1.0); // back
    vec3 colOut = occ*lightColor*(ambient+diffuse*shadow+.25 +back) + vec3(.7,.9,1)*specular*specular;
    return colOut;
}

vec3 getColorTextura( vec3 p, vec3 nor,  int i)
{
    vec3 col;
    if (i==100 )
    {  col=tex3D(iChannel0, p/32., nor); return col*2.0; }
    if (i==105 ) { col=tex3D(iChannel0, p/32., nor); col=vec3(1.0)-col;  return  col;  }
    if (i==106 ) { col=tex3D(iChannel0, p/16., nor); col=vec3(1.0,1.0,0.0)-col;  return  col;  }

}

vec3 render_sky_color(vec3 rd)
{   float t = (rd.x + 1.0) / 2.0;
    vec3 col= vec3((1.0 - t) + t * 0.3, (1.0 - t) + t * 0.5, (1.0 - t) + t);
    vec3  sky = mix(vec3(.0, .1, .4)*col, vec3(.3, .6, .8), 1.0 - rd.y);
    float d = pow(rd.z+0.3,2.)*pow(rd.z*0.5+0.5,1.)/pow(1.3,2.)*0.9+0.1;
	//return sky;
    return sky*d;
}

vec3 GetMaterial(vec3 p,  vec3 nor, vec3 ro,  vec3 rd, int id_color)
{  	vec3 colobj; 
     if (id_color>=100 )
   { return  getColorTextura( p, nor,id_color); }      
}

vec3 linear2srgb(vec3 c) 
{ return mix(12.92 * c,1.055 * pow(c, vec3(1.0/1.8)) - 0.055, step(vec3(0.0031308), c)); }

vec3 exposureToneMapping(float exposure, vec3 hdrColor) 
{ return vec3(1.0) - exp(-hdrColor * exposure); }

vec3 ACESFilm(vec3 x)
{   float a,b,c,d,e;
    a = 2.51; b = 0.03; c = 2.43;d = 0.59; e = 0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}

vec3 Render(vec3 ro, vec3 rd)
{  vec3 col = vec3(0);
  vec3 p;
     vec2 hit=RayMarch(ro,rd, MAX_STEPS);
      if(hit.x<MAX_DIST)
       {   p = (ro + rd * hit.x );
        vec3 nor=GetNormal(p);
        vec3 colobj;
        colobj=GetMaterial( p, nor, ro, rd,  int(hit.y));
        vec3 result;
         result= lightingv3(nor, p,light_pos1, rd,ro,colobj,hit.x)*light_color1;
        result+= lightingv3(nor, p,light_pos2,rd, ro,colobj,hit.x)*light_color2;
        col= result/2.0;
        col= (ACESFilm(col)+linear2srgb(col)+col+ exposureToneMapping(3.0, col))/4.0 ;

    }
    else if(hit.x>MAX_DIST)
    col= render_sky_color(rd);
   return col;
}


Ray RotarEnCirculo(vec3 ro,vec3 rd)
{
   float r=35.0;
   float veltime=30.0;  
    float tt = radians( -iTime*veltime);
    vec3  pos=vec3(0.0,0.0,20.0);
    vec3 rotation1 = pos+vec3(r*sin(tt), 0.0, r*cos(tt));
    ro +=rotation1;   
    rd=rotate_y( vec3(-rd.x,rd.y,-rd.z), tt);
    return  Ray(ro,rd);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float t;
    t=mod(iTime*1.0,360.0);
 	light_pos1= vec3(-10.0, 120.0, -25.); light_color1=vec3( 1.0,1.0,1.0 );
 	light_pos2= vec3(10.0, 20.0, -25.0 ); light_color2 =vec3( 0.5,0.5,0.5 ); 
   vec3 ro=vec3(0.0,25.0+5.0*sin(t),-25.0);
   vec3 rd=normalize( vec3(uv.x,uv.y,1.0));

   rd= rotate_x(rd, radians(30.));
     Ray ray= RotarEnCirculo(ro,rd);
    ro=ray.ro;
    rd=ray.rd;

   rd= rotate_x(rd, radians(20.)); 

    light_pos1+=ro;
    light_pos2+=ro;
    vec3 col= Render( ro,  rd);
    fragColor = vec4(col,1.0);
}

