// Modified version of font loader from https://www.shadertoy.com/view/ltcXzs
void drawChar(inout vec3 color, in vec3 charColor, in vec2 uv, in vec2 pos, in vec2 size, in int char) {
    uv = (uv - pos) / size + 0.5;
    vec2 charUv = uv / 16.0;
    vec2 dfdx = dFdx(charUv);
    vec2 dfdy = dFdy(charUv);
    if (all(lessThan(abs(uv - 0.5), vec2(0.5)))) {
        float val = textureGrad(iChannel0, charUv + fract(vec2(char, 15 - char / 16) / 16.0), dfdx, dfdy).r;
        color = mix(color, charColor, val);
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Default shader
    vec2 uv = fragCoord / iResolution.xy;
    vec3 color = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0.0, 2.0, 4.0));

    // Aspect correct, centered UVs and 2 pixel AA width for soft edges
    uv = fragCoord / iResolution.y - vec2(0.0, 0.5);
    float unit = 2.0 / iResolution.y;

    // Graph sound wave (MUCH slower than actual speed)
    float x = mod(0.02 * (uv.x + 2.0 * iTime), 11.0);
    float graphDist = abs(uv.y - 0.5 * makeNoise(x));
    float slope = 0.02 * (makeNoise(x + 0.0001) - makeNoise(x - 0.0001)) / 0.0002;
    graphDist /= sqrt(slope * slope + 1.0);
    graphDist += 0.0025;

    color = mix(color, vec3(0.0), smoothstep(unit, 0.0, graphDist));

    fragColor = vec4(color, 1.0);

    // Uncenter
    uv += vec2(0.0, 0.5);

    // "Sound In" notification
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.3, 0.3), vec2(0.1), 83); // S
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.4, 0.3), vec2(0.1), 79); // O
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.5, 0.3), vec2(0.1), 85); // U
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.6, 0.3), vec2(0.1), 78); // N
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.7, 0.3), vec2(0.1), 68); // D

    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(0.9, 0.3), vec2(0.1), 73); // I
    drawChar(fragColor.rgb, vec3(1.0), uv, vec2(1.0, 0.3), vec2(0.1), 78); // N
}

#define TAU 6.2831853072

// https://www.shadertoy.com/view/4djSRW
float Hash11(in float p) {
    p = fract(p * 0.1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

float snoise(in float x) {
    return mix(Hash11(floor(x)), Hash11(ceil(x)), smoothstep(0.0, 1.0, fract(x)));
}

float makeNoise(in float time) {
    float sound = 0.0;

    float t = 5.0 * time;
    //float n = max(2.0, 6.0 * abs(mod(0.05 * floor(t) * floor(t), 2.0) - 1.0));
    //float n = max(2.0, 6.0 * abs(mod(0.2 * pow(floor(t), 1.5), 2.0) - 1.0));
    //float n = floor(6.0 * snoise(0.25 * floor(t))) + 2.0;

    // Randomly hacked together
    float s = floor(t);
    float n = mod(s, 6.0) + mod(s, 2.0) + 2.0;
    n += mod(s, mix(2.0, 4.0, step(1.0, mod(time, 5.0))));
    n += floor(6.0 * snoise(0.25 * s));
    n = floor(0.5 * n) + 1.0;

    // Smooth transition to avoid clicks
    t = fract(t);
    float env = smoothstep(0.0, 0.08, min(t, 1.0 - t));

    // Random frequencies that sound good together
    // The gaussian weight filters out high frequencies
    for (float i=0.0; i < 50.0; i++) {
        float freq = floor(550.0 * floor(Hash11(i) * n) / n);
        sound += sin(TAU * fract(freq * time)) * env * exp(-0.00001 * freq * freq) / 25.0;
    }

    return sound;
}

vec2 mainSound(in int samp, in float time) {
    return vec2(makeNoise(float(samp % (11 * int(iSampleRate))) / iSampleRate));
}
