float Band(float t, float start, float end, float blur)
{
    float step1 = smoothstep(start - blur, start + blur, t);
    float step2 = smoothstep(end + blur, end - blur, t);
    return step1 * step2;
}

float Rect(vec2 uv, float left, float right, float top, float bottom, float blur)
{
    float band1 = Band(uv.x, left, right, blur);
    float band2 = Band(uv.y, top, bottom, blur);
    return band1 * band2;
}


float Remap(float a, float b, float c, float d, float t)
{
    return (clamp(0., 1., (t - a) / (b - a))) * (d - c) + c;
}

vec3  RainbowBand(vec2 uv, vec2 p, float t, float t_col)
{
    uv -= p;

    float x = uv.x;
    float m = 0.1*sin(t * 2.+ x * 10.);
    float y = uv.y - m;

    float blur = Remap(-.8, .8, .0, .5, x);
    blur = blur * blur * blur * blur;
    float blur2 = Remap(-.8, .8, .5, .0, x);
    blur2 = blur2 * blur2 * blur2 * blur2;
    blur = blur + blur2;

    float mask = Rect(vec2(x,y), -.8, .8, -.05, .05, blur);

    vec3 color = vec3(1.,0.,0.);
    float c_sin = sin(t_col + x * 10.);
    float c_m = Remap(-1., 1., .5, 1., c_sin);
    color = mix(color, vec3(.0,1.,0.) * c_m, smoothstep(-.4, .0, uv.x * .5));
    color = mix(color, vec3(.0,0.,1.) * c_m, smoothstep(.0, .4, uv.x * .5));
    color *= mask;

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv -= vec2(0.5);
    uv.x *= iResolution.x/iResolution.y;

    float x = uv.x;
    float t = iTime;
    float m = 0.1*sin(t * 2.+ x * 10.);
    float y = uv.y - m;

    vec3 color = vec3(.0);
    int base_band_count = 8; // INPUT, change this.
    float frequency = 2.;

    float b = sin(t * frequency);
    b = Remap(-1., 1., 1., float(base_band_count + 1), b);
    int band_count = int(b);


    float offset_y = float(band_count - 1) * .5;
    for(int i = 0; i < band_count; i++)
    {
        float k = float(i) - offset_y;
        color += RainbowBand(uv, vec2(.0, k * .15), t, t * k * 10.);
    }

    fragColor = vec4(color, 1.);
}
