#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

float map(vec3 p){
    p -= vec3(2.0, 0.0, 0.0);
    p.xy*=rot(iTime*1.);
    p.xz*=rot(iTime*1.);
    // This is a 2D SDF to 3D trick.
    // 1. Calculate 2D SDF
    float sdf2d=abs(length(p.xy)-1.)-.2;
    // 2. Calculate 1D SDF
    float d=abs(p.z)-.3;
    // 3. Combie them by max(boolean)
    return max(sdf2d,d);
}


float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) +
         length(max(d,0.0));
}


// from: https://gam0022.net/blog/2017/03/02/raymarching-fold/
vec3 foldX(vec3 p) {
    // This is the trick to make a mirror
    p.x = abs(p.x);
    return p;
}

float dTree(vec3 p) {
    vec3 size = vec3(0.1, 1.0, 0.1);
    float d = sdBox(p, size);
    p = foldX(p);
    p.y -= 0.1;
    p.xy *= rot(-1.2); // angle of arm
    d = min(d, sdBox(p, size));
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(gl_FragCoord.xy-.5*iResolution.xy)/iResolution.y;
    vec3 rd=normalize(vec3(uv,1));
    vec3 p=vec3(0.0,0,-5);
    float d=1.,i;
    for(;++i<99.&&d>.001;)p+=rd*(d=dTree(foldX(p)));
    if(d<.001)fragColor+=3./i;
}
