vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+vec2(0,1.5))*scale)-.5);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col;  
    float t1 = 3.;
    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    float scale = 1.5;
    for(int i=0;i<6;i++)
    {
        uv = triangle_wave(uv,scale);
        //uv -= col.x*uv.x; //makes a nice "gilded" pattern
        uv = triangle_wave(uv.yx,scale)+triangle_wave(uv-.5,scale);
        col.x = (uv.x+uv.y);
        col = abs(col.yzx-col);

        //animated jiggly thing
        //uv.x += col.x*uv.y/(2.+triangle_wave(uv.yx+iTime,scale).x);
    }
    fragColor = vec4(min(vec3(1.),col),1.0);
}
