vec3 BLUE = vec3(12, 85, 150)/255.;
vec3 YELLOW = vec3(253, 195, 11)/255.;



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;



    float w = fwidth(uv.x);

    float left = smoothstep(0.3, 0.3+w, uv.x);
    float right = smoothstep(0.45, 0.45-w, uv.x);


    float top = smoothstep(0.4, 0.4+w, uv.y);
    float bottom = smoothstep(0.6, 0.6-w, uv.y);



    float color = clamp(left*right + top*bottom, 0., 1.);



    vec3 col = mix(BLUE, YELLOW, color);


    vec3 c = vec3(color);

    fragColor = vec4(col,1.0);
}
