// Distance to a line segment in the L-infinity metric space.
// Like in the regular L2 metric space, it boils down to a
// clamped projection and a distance measure.
//
// List of some other 2D distances in L-infinity:
//
// https://www.shadertoy.com/playlist/XXccDH


float sdLine( in vec2 p, vec2 a, vec2 b )
{
    vec2 pa = p-a;
    vec2 ba = b-a;

    float s = (ba.x*ba.y>0.0)?1.0:-1.0;
    float h = clamp( (pa.y+s*pa.x)/(ba.y+s*ba.x), 0.0, 1.0 );

    vec2 q = abs(pa-h*ba);
    return max(q.x,q.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (2.0*fragCoord - iResolution.xy) / iResolution.y;
    vec2 m = (2.0*iMouse.xy - iResolution.xy) / iResolution.y;

    if( iMouse.z<0.01 ) m=sin(-0.15*iTime*vec2(1.1,1.3)+vec2(0,2));

    vec2 v1 = vec2(1.0,0.8)*cos( 0.1*iTime + vec2(0.0,1.00) + 0.0 );
	vec2 v2 = vec2(1.0,0.8)*cos( 0.1*iTime + vec2(0.0,3.00) + 1.5 );

    float d = sdLine(p, v1, v2);

    vec3 col = vec3(1.0,0.9,1.0) + sign(d)*vec3(-0.3,0.4,0.3);
	col *= 1.0 - exp(-3.0*abs(d));
	col *= 0.8 + 0.2*cos(150.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.008,abs(d)) );


    d = sdLine(m, v1, v2);
     col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.004, 0.008, abs(max(abs(p.x-m.x), abs(p.y-m.y)) - abs(d))));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.004, 0.008, length(p-m)-0.015));

	fragColor = vec4(col,1.0);
}
