#define M_PI 3.1415926535897932384626433832795

vec2 phyllotaxis(float i, float c) {
    float phi = M_PI * (3. - sqrt(5.)) * c;
    /// 计算出的 xy 呈 “螺旋线” 的形状
    float angle = (i / c) * phi;
    float r = sqrt(i / c);
    float x = r * cos(angle);
    float y = r * sin(angle);

    return vec2(x, y);
}

float samples = 128.;
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    vec4 col;
    float pre = fract(iTime *0.2);
    for(float i = 0.; i < samples; i++) {   
         // 选用的是螺旋线采样
        vec2 offset;
        float angle = pre* M_PI;
        //原理是 通过sin函数的0 ~ PI 来实现 0 ~ PI/2让currentImg渐变模糊，处于PI/2时切换纹理，PI/2 ~ PI 让nextImg渐变清晰
        //一直循环播放看起来效果不是很好，本人自用于两张图片的切换效果
        if(angle<=M_PI/2.0) {
            offset = phyllotaxis(i, samples) * 0.1* sin(angle);
            col += texture(iChannel0, uv + offset);
        } else {
            offset = phyllotaxis(i, samples)  * 0.1 * sin(angle);
            col += texture(iChannel1, uv + offset);
        }
    }
    col /= samples;
    fragColor = vec4(col.rgb, 1.0);
}
