// "Block Impact" by dr2 - 2021
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

// (Based on "Domino Towers", with coloring from "Spectral Duck")

#define VAR_ZERO min (iFrame, 0)

float PrBox2Df (vec2 p, vec2 b);
mat3 QtToRMat (vec4 q);
float Minv2 (vec2 p);
float Minv3 (vec3 p);
float Maxv3 (vec3 p);
vec2 Rot2D (vec2 q, float a);
mat3 StdVuMat (float el, float az);
vec3 HsvToRgb (vec3 c);
float Fbm2 (vec2 p);
vec3 VaryNf (vec3 p, vec3 n, float f);
vec4 Loadv4 (int idVar);

vec3 blkGap, bSize, fcBlk, ltDir;
vec2 qBlk;
float tCur, dstFar, nRun;
int idObj;
const float pi = 3.1415927;

const int nObj = 97;

const ivec3 nSphObjD = ivec3 (6, 3, 3);
const int nSphObj = nSphObjD.x * nSphObjD.y * nSphObjD.z;
const int nSphTot = nObj * nSphObj;
const int nPre = 2;

vec3 GetR (int k)
{
  return Loadv4 (nPre + 4 * k).xyz;
}

vec4 GetQ (int k)
{
  return Loadv4 (nPre + 4 * k + 2);
}

vec4 BlkHit (vec3 ro, vec3 rd, vec3 bSize)
{
  vec3 v, tm, tp, fId, u;
  float dMin, dn, df;
  dMin = dstFar;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) {
    dMin = dn;
    u = (v + dn) * rd;
    fId = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
    qBlk = vec2 (dot (u.zxy, fId), dot (u.yzx, fId));
  }
  return vec4 (dMin, fId);
}

float BlkHitSh (vec3 ro, vec3 rd, vec3 bSize)
{
  vec3 v, tm, tp;
  float dMin, dn, df;
  dMin = dstFar;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) dMin = dn;
  return dMin;
}

float ObjHit (vec3 ro, vec3 rd)
{
  mat3 mRot;
  vec4 cdn4;
  vec3 rm, rom, rdm;
  vec2 qBlkN;
  float dMin;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nObj; n ++) {
    rm = GetR (n);
    mRot = QtToRMat (GetQ (n));
    rom = (ro - rm) * mRot;
    rdm = rd * mRot;
    cdn4 = BlkHit (rom, rdm, bSize);
    if (cdn4.x < dMin) {
      dMin = cdn4.x;
      fcBlk = cdn4.yzw;
      qBlkN = qBlk;
      idObj = n;
    }
  }
  qBlk = qBlkN;
  return dMin;
}

float ObjHitSh (vec3 ro, vec3 rd, float rng)
{
  mat3 mRot;
  vec3 rm, rom, rdm;
  float dMin;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nObj; n ++) {
    rm = GetR (n);
    mRot = QtToRMat (GetQ (n));
    rom = (ro - rm) * mRot;
    rdm = rd * mRot;
    dMin = min (dMin, BlkHitSh (rom, rdm, bSize));
  }
  return smoothstep (0.5 * rng, rng, dMin);
}

vec3 ObjCol (vec3 rd, float sh)
{
  mat3 mRot;
  vec3 q, vn, col;
  float ep;
  vn = fcBlk;
  mRot = QtToRMat (GetQ (idObj));
  col = HsvToRgb (vec3 (mod (- dot (rd, mRot * vn), 1.), 0.5, 1.));
  ep = smoothstep (0., 0.2, PrBox2Df (qBlk, ((abs (vn.x) == 1.) ? bSize.zy :
     ((abs (vn.y) == 1.) ? bSize.xz : bSize.yx)) - 0.2));
  if (abs (vn.y) == 1.) ep = max (ep, 1. - smoothstep (0., 0.1,
     abs (PrBox2Df (qBlk, 0.5 * bSize.xz)) - 0.05));
  col *= 1. - 0.3 * ep;
  if (ep > 0.) {
    q = vec3 (qBlk, 0.);
    if (abs (vn.x) == 1.) q = q.zxy;
    else if (abs (vn.y) == 1.) q = q.xzy;
    vn = VaryNf (32. * q, vn, 0.7);
  }
  vn = mRot * vn;
  col = col * (0.2 +  0.8 * sh * max (dot (vn, ltDir), 0.)) +
     0.2 * sh * step (0.95, sh) * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn;
  float dstObj, sh;
  bool isBg;
  bSize = 0.5 * blkGap * (vec3 (nSphObjD) - 1.) + 0.4;
  dstObj = ObjHit (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    isBg = false;
  } else {
    if (rd.y < 0.) ro += ((0.5 - ro.y) / rd.y) * rd;
    isBg = true;
  }
  if (! isBg || isBg && rd.y < 0.) sh = 0.6 + 0.4 * ObjHitSh (ro + 0.01 * ltDir, ltDir, 60.);
  if (! isBg) col = ObjCol (rd, sh);
  else if (rd.y < 0.) {
    vn = vec3 (0., 1., 0.);
    col = mix (vec3 (0.8, 0.8, 0.75), vec3 (0.8, 0.8, 0.85),
       smoothstep (0., 0.1, Fbm2 (0.1 * ro.xz) - 0.4));
    col *= 0.2 + 0.8 * sh * max (dot (vn, ltDir), 0.);
    col *= 1. - 0.9 * smoothstep (0.9, 1., length (ro.xz) / dstFar);
  } else col = vec3 (0.1);
  if (isBg && rd.y < 0.) {
    rd = reflect (rd, vn);
    ro += 0.01 * rd;
    dstObj = ObjHit (ro, rd);
    if (dstObj < dstFar) {
      ro += dstObj * rd;
      col = mix (col, ObjCol (rd, 1.), 0.3);
    }
  }
  return clamp (col, 0., 1.);
}

#define AA    0

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 stDat, mPtr;
  vec3 col, rd, ro;
  vec2 canvas, uv;
  float az, el, asp, zmFac, sr, h;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  asp = canvas.x / canvas.y;
  stDat = Loadv4 (0);
  blkGap = stDat.yzw;
  stDat = Loadv4 (1);
  mPtr.xyz = stDat.xyz;
  nRun = stDat.w;
  dstFar = 300.;
  az = -0.2 * pi;
  el = -0.12 * pi;
  if (mPtr.z > 0. && Minv2 (mPtr.xy + 0.05 * vec2 (1. / asp, 1.) - 0.5) < 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  }
  el = clamp (el, -0.48 * pi, -0.05 * pi);
  vuMat = StdVuMat (el, az);
  ro = vec3 (0., 0., -120.);
  h = 0.;
  for (int n = VAR_ZERO; n < nObj; n ++) h += GetR (n).y;
  ro.y = min (h / float (nObj), 10.);
  ro = vuMat * ro;
  zmFac = 5.3;
  ltDir = vuMat * normalize (vec3 (0.6, 0.5, -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  if (mPtr.z > 0. && Minv2 (uv - vec2 (asp, 1.)) > -0.1) col = mix (col, vec3 (0.5, 1., 1.), 0.3);
  fragColor = vec4 (col, 1.);
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.) - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 256.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

// "Block Impact" by dr2 - 2021
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

#define VAR_ZERO min (iFrame, 0)

vec4 QtMul (vec4 q1, vec4 q2);
vec4 RotToQt (vec3 v, float a);
mat3 QtToRMat (vec4 q);
vec4 RMatToQt (mat3 m);
mat3 LpStepMat (vec3 a);
float Maxv2 (vec2 p);
vec2 Rot2D (vec2 q, float a);
float Hashff (float p);
vec4 Loadv4 (int idVar);
void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord);

vec3 blkGap;
float todCur, nStep, nRun, farSep;
const float pi = 3.1415927;
const float txRow = 256.;

#define PAR_SPH 1  // faster (uses extra parallelism, but needs 3 calls to complete)

const int nObj = 97;

const ivec3 nSphObjD = ivec3 (6, 3, 3);
const int nSphObj = nSphObjD.x * nSphObjD.y * nSphObjD.z;
const int nSphTot = nObj * nSphObj;
const int nPre = 2;

vec3 GetR (int k)
{
  return Loadv4 (nPre + 4 * k).xyz;
}

vec3 GetV (int k)
{
  return Loadv4 (nPre + 4 * k + 1).xyz;
}

vec4 GetQ (int k)
{
  return Loadv4 (nPre + 4 * k + 2);
}

vec3 GetW (int k)
{
  return Loadv4 (nPre + 4 * k + 3).xyz;
}

#if PAR_SPH

vec3 GetAS (int k)
{
  return Loadv4 (nPre + 4 * nObj + 2 * k).xyz;
}

vec3 GetWAS (int k)
{
  return Loadv4 (nPre + 4 * nObj + 2 * k + 1).xyz;
}

vec3 GetRS (int k)
{
  return Loadv4 (nPre + 4 * nObj + 2 * nSphTot + 2 * k).xyz;
}

vec3 GetVS (int k)
{
  return Loadv4 (nPre + 4 * nObj + 2 * nSphTot + 2 * k + 1).xyz;
}

#endif

vec3 RSph (float s)
{
  vec3 b;
  b = vec3 (nSphObjD);
  return blkGap * (floor (vec3 (mod (s, b.x), mod (s, b.x * b.y) / b.x,
      s / (b.x * b.y))) - 0.5 * (b - 1.));
}

vec3 FcFun (vec3 dr, float rSep, vec3 dv)
{
  vec3 f;
  float rSepI, vRel, fo, drv;
  const float fOvlap = 500., fricN = 1., fricT = 2., fricS = 10.;
  rSepI = 1. / rSep;
  fo = fOvlap * (rSepI - 1.);
  drv = dot (dr, dv) * rSepI * rSepI;
  dv -= drv * dr;
  vRel = length (dv);
  fo = max (fo - fricN * drv, 0.);
  f = fo * dr;
  if (vRel > 0.001) f -= min (fricT, fricS * abs (fo) * rSep / vRel) * dv;
  return f;
}

#if PAR_SPH

void StepRS (int sId, out vec3 rms, out vec3 vms)
{
  vec3 rs;
  int mId;
  mId = sId / nSphObj;
  rs = QtToRMat (GetQ (mId)) * RSph (float (sId - mId * nSphObj));
  rms = GetR (mId) + rs;
  vms = GetV (mId) + cross (GetW (mId), rs);
}

void InitRS (int sId, out vec3 rms, out vec3 vms)
{
  rms = vec3 (0.);
  vms = vec3 (0.);
}

void StepAS (int sId, out vec3 am, out vec3 wam)
{
  vec3 dr, rm, rms, vms, fc;
  float rSep;
  int mId, sIdN;
  mId = sId / nSphObj;
  rm = GetR (mId);
  rms = GetRS (sId);
  vms = GetVS (sId);
  am = vec3 (0.);
  wam = vec3 (0.);
  for (int mIdN = VAR_ZERO; mIdN < nObj; mIdN ++) {
    if (mIdN != mId && length (rms - GetR (mIdN)) < farSep) {
      for (int j = VAR_ZERO; j < nSphObj; j ++) {
        sIdN = mIdN * nSphObj + j;
        dr = rms - GetRS (sIdN);
        rSep = length (dr);
        if (rSep < 1.) {
          fc = FcFun (dr, rSep, vms - GetVS (sIdN));
          am += fc;
          wam += cross (rms - rm, fc);
        }
      }
    }
  }
  rSep = abs (rms.y);
  if (rSep < 1.) {
    fc = FcFun (vec3 (0., rms.y, 0.), rSep, vms);
    am += fc;
    wam += cross (rms - rm, fc);
  }
}

void InitAS (int sId, out vec3 am, out vec3 wam)
{
  am = vec3 (0.);
  wam = vec3 (0.);
}

#endif

void StepM (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  mat3 mRot;
  vec3 dr, rs, am, wam, rMom;
  float rSep, grav, dt;
#if ! PAR_SPH
  mat3 mRotN;
  vec3 rmN, vmN, wmN, rsN, dv, rms, vms, fc;
#endif
  grav = 5.;
  dt = 0.01;
  rm = GetR (mId);
  vm = GetV (mId);
  qm = GetQ (mId);
  wm = GetW (mId);
  mRot = QtToRMat (qm);
  am = vec3 (0.);
  wam = vec3 (0.);
#if ! PAR_SPH
  for (int n = VAR_ZERO; n < nObj; n ++) {
    rmN = GetR (n);
    if (n != mId && length (rm - rmN) < farSep) {
      vmN = GetV (n);
      mRotN = QtToRMat (GetQ (n));
      wmN = GetW (n);
      for (int j1 = VAR_ZERO; j1 < nSphObj; j1 ++) {
        rs = mRot * RSph (float (j1));
        rms = rm + rs;
        vms = vm + cross (wm, rs);
        dv = vms - vmN;
        fc = vec3 (0.);
        for (int j2 = VAR_ZERO; j2 < nSphObj; j2 ++) {
          rsN = mRotN * RSph (float (j2));
          dr = rms - (rmN + rsN);
          rSep = length (dr);
          if (rSep < 1.) fc += FcFun (dr, rSep, dv - cross (wmN, rsN));
        }
        am += fc;
        wam += cross (rs, fc);
      }
    }
  }
  for (int j = VAR_ZERO; j < nSphObj; j ++) {
    rs = RSph (float (j));
    rs = mRot * rs;
    dr = rm + rs;
    rSep = abs (dr.y);
    if (rSep < 1.) {
      fc = FcFun (vec3 (0., dr.y, 0.), rSep, vm + cross (wm, rs));
      am += fc;
      wam += cross (rs, fc);
    }
  }
#else
  for (int j = VAR_ZERO; j < nSphObj; j ++) {
    am += GetAS (mId * nSphObj + j);
    wam += GetWAS (mId * nSphObj + j);
  }
#endif
  rMom = vec3 (0.);
  for (int j = VAR_ZERO; j < nSphObj; j ++) {
    rs = RSph (float (j));
    rMom += dot (rs, rs) - rs * rs + 1./6.;
  }
  rMom /= float (nSphObj);
  wam = mRot * (wam * mRot / rMom);
  am.y -=  grav;
  if (nStep > 20.) {
    vm += dt * am;
    rm += dt * vm;
    wm += dt * wam;
    qm = normalize (QtMul (RMatToQt (LpStepMat (0.5 * dt * wm)), qm));
  }
}

void InitM (int mId, out vec3 rm, out vec3 vm, out vec4 qm, out vec3 wm)
{
  vec3 bSize;
  float bsx, bsz, sy, fId, nb12, a, s, t;
  bSize = 0.5 * blkGap * (vec3 (nSphObjD) - 1.) + 0.4;
  bsx = bSize.x + 0.1;
  bsz = bSize.z + 0.15;
  if (mId < nObj - 1) {
    fId = float (mId);
    nb12 = floor (fId / 12.);
    rm.y = (bSize.y + 0.1) * (2. * nb12 + 1.) + 0.5;
    sy = 2. * mod (nb12, 2.) - 1.;
    fId = mod (fId, 12.);
    qm = RotToQt (vec3 (0., 1., 0.), 0.5 * pi * step (6., fId));
    if (fId < 6.) {
      s = sign (fId - 2.5);
      rm.x = (mod (fId, 3.) - 1.) * 2. * bsx - s * sy * bsz;
      rm.z = 3. * s * bsx;
    } else {
      s = sign (fId - 8.5);
      rm.x = 3. * s * bsx;
      rm.z = (mod (fId, 3.) - 1.) * 2. * bsx + s * sy * bsz;
    }
    vm = vec3 (0.);
  } else {
    t = 3.1 * floor (0.1 * todCur);
    rm = vec3 (40., 5. + 5. * Hashff (t + 11.1 * nRun), 0.);
    vm = vec3 (- (30. + 60. * Hashff (t + 21.1 * nRun)), 5., 0.);
    a = pi * (Hashff (t + 31.1 * nRun) - 0.75);
    rm.xz = Rot2D (rm.xz, a);
    vm.xz = Rot2D (vm.xz, a);
    qm = RotToQt (vec3 (0., 1., 0.), - a);
  }
  wm = vec3 (0.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr, mPtrP, stDat, qm;
  vec3 rm, vm, wm, am, wam;
  vec2 iFrag, canvas;
  float asp, mxStep, nSubStep;
  int mId, pxId, kp;
#if PAR_SPH
  vec3 rms, vms;
  int sId, rId;
#endif
  bool doInit;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
#if PAR_SPH
  if (iFrag.x >= txRow || pxId >= nPre + 4 * nObj + 4 * nSphTot) discard;
#else
  if (iFrag.x >= txRow || pxId >= nPre + 4 * nObj) discard;
#endif
  canvas = iResolution.xy;
  todCur = iDate.w;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  kp = nPre + 4 * nObj;
  mId = (pxId >= nPre && pxId < kp) ? (pxId - nPre) / 4 : -1;
#if PAR_SPH
  sId = (pxId >= kp && pxId < kp + 2 * nSphTot) ? (pxId - kp) / 2 : -1;
  rId = (pxId >= kp + 2 * nSphTot) ? (pxId - (kp + 2 * nSphTot)) / 2 : -1;
#endif
  doInit = false;
  mxStep = 8000.;
  if (iFrame <= 5) {
    doInit = true;
    nRun = 0.;
    blkGap = vec3 (0.9, 0.9, 0.9);
  } else {
    stDat = Loadv4 (0);
    nStep = stDat.x;
    blkGap = stDat.yzw;
    stDat = Loadv4 (1);
    mPtrP.z = stDat.z;
    nRun = stDat.w;
    ++ nStep;
    if (nStep > mxStep || nStep > 100. && mPtr.z > 0. && mPtrP.z < 0. &&
       Maxv2 (mPtr.xy - 0.5 - vec2 (0.05 / asp, - 0.05)) > 0.) doInit = true;
  }
  if (doInit) {
    ++ nRun;
    nStep = 0.;
  }
  doInit = (doInit || nStep < 20.);
  nSubStep = mod (nStep, 3.);
  farSep = length (blkGap * (vec3 (nSphObjD) - 1.)) + 1.;
#if PAR_SPH
  if (! doInit && (pxId >= nPre + 4 * nObj && (nSubStep == 2. || nSubStep == 1. && sId < 0 ||
     nSubStep == 0. && rId < 0))) discard;
#endif
  if (mId >= 0) {
    if (doInit) InitM (mId, rm, vm, qm, wm);
    else {
#if PAR_SPH
      if (nSubStep == 2.) StepM (mId, rm, vm, qm, wm);
      else {
        rm = GetR (mId);
        vm = GetV (mId);
        qm = GetQ (mId);
        wm = GetW (mId);
      }
#else
      StepM (mId, rm, vm, qm, wm);
#endif
    }
    kp = nPre + 4 * mId;
    if      (pxId == kp + 0) stDat = vec4 (rm, 0.);
    else if (pxId == kp + 1) stDat = vec4 (vm, 0.);
    else if (pxId == kp + 2) stDat = qm;
    else if (pxId == kp + 3) stDat = vec4 (wm, 0.);
#if PAR_SPH
  } else if (sId >= 0) {
    if (doInit) InitAS (sId, am, wam);
    else StepAS (sId, am, wam);
    kp = nPre + 4 * nObj + 2 * sId;
    if      (pxId == kp + 0) stDat = vec4 (am, 0.);
    else if (pxId == kp + 1) stDat = vec4 (wam, 0.);
  } else if (rId >= 0) {
    if (doInit) InitRS (rId, rms, vms);
    else StepRS (rId, rms, vms);
    kp = nPre + 4 * nObj + 2 * nSphTot + 2 * rId;
    if      (pxId == kp + 0) stDat = vec4 (rms, 0.);
    else if (pxId == kp + 1) stDat = vec4 (vms, 0.);
#endif
  } else {
    if      (pxId == 0) stDat = vec4 (nStep, blkGap);
    else if (pxId == 1) stDat = vec4 (mPtr.xyz, nRun);
  }
  Savev4 (pxId, stDat, fragColor, fragCoord);
}

vec4 QtMul (vec4 q1, vec4 q2)
{
  return vec4 (
       q1.w * q2.x - q1.z * q2.y + q1.y * q2.z + q1.x * q2.w,
       q1.z * q2.x + q1.w * q2.y - q1.x * q2.z + q1.y * q2.w,
     - q1.y * q2.x + q1.x * q2.y + q1.w * q2.z + q1.z * q2.w,
     - q1.x * q2.x - q1.y * q2.y - q1.z * q2.z + q1.w * q2.w);
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  float a1, a2, s;
  q = normalize (q);
  s = q.w * q.w - 0.5;
  m[0][0] = q.x * q.x + s;  m[1][1] = q.y * q.y + s;  m[2][2] = q.z * q.z + s;
  a1 = q.x * q.y;  a2 = q.z * q.w;  m[0][1] = a1 + a2;  m[1][0] = a1 - a2;
  a1 = q.x * q.z;  a2 = q.y * q.w;  m[2][0] = a1 + a2;  m[0][2] = a1 - a2;
  a1 = q.y * q.z;  a2 = q.x * q.w;  m[1][2] = a1 + a2;  m[2][1] = a1 - a2;
  return 2. * m;
}

vec4 RMatToQt (mat3 m)
{
  vec4 q;
  const float tol = 1e-6;
  q.w = 0.5 * sqrt (max (1. + m[0][0] + m[1][1] + m[2][2], 0.));
  if (abs (q.w) > tol) q.xyz =
     vec3 (m[1][2] - m[2][1], m[2][0] - m[0][2], m[0][1] - m[1][0]) / (4. * q.w);
  else {
    q.x = sqrt (max (0.5 * (1. + m[0][0]), 0.));
    if (abs (q.x) > tol) q.yz = vec2 (m[0][1], m[0][2]) / q.x;
    else {
      q.y = sqrt (max (0.5 * (1. + m[1][1]), 0.));
      if (abs (q.y) > tol) q.z = m[1][2] / q.y;
      else q.z = 1.;
    }
  }
  return normalize (q);
}

vec4 RotToQt (vec3 v, float a)
{
  vec4 q;
  float c, s;
  const float tol = 1e-6;
  c = cos (a);  s = sin (a);
  q.w = 0.5 * sqrt (max (1. + (1. - c) * dot (v, v) + 3. * c, 0.));
  if (q.w > tol) q.xyz = s * v.xyz / (2. * q.w);
  else {
    q.x = sqrt (max (0.5 * (1. + (1. - c) * v.x * v.x + c), 0.));
    if (q.x > tol) q.yz = ((1. - c) * v.xz * v.yx + s * v.zy) / q.x;
    else {
      q.y = sqrt (max (0.5 * (1. + (1. - c) * v.y * v.y + c), 0.));
      q.z = (q.y > tol) ? ((1. - c) * v.y * v.z + s * v.x) / q.y : 1.;
    }
  }
  return q;
}

mat3 LpStepMat (vec3 a)
{
  mat3 m1, m2;
  vec3 t, c, s;
  float b1, b2;
  t = 0.25 * a * a;
  c = (1. - t) / (1. + t);
  s = a / (1. + t);
  m1[0][0] = c.y * c.z;  m2[0][0] = c.y * c.z;
  b1 = s.x * s.y * c.z;  b2 = c.x * s.z;
  m1[0][1] = b1 + b2;  m2[1][0] = b1 - b2;
  b1 = c.x * s.y * c.z;  b2 = s.x * s.z;
  m1[0][2] = - b1 + b2;  m2[2][0] = b1 + b2;
  b1 = c.y * s.z;
  m1[1][0] = - b1;  m2[0][1] = b1;  
  b1 = s.x * s.y * s.z;  b2 = c.x * c.z;
  m1[1][1] = - b1 + b2;  m2[1][1] = b1 + b2; 
  b1 = c.x * s.y * s.z;  b2 = s.x * c.z;
  m1[1][2] = b1 + b2;  m2[2][1] = b1 - b2;
  m1[2][0] = s.y;  m2[0][2] = - s.y;
  b1 = s.x * c.y;
  m1[2][1] = - b1;  m2[1][2] = b1;
  b1 = c.x * c.y;
  m1[2][2] = b1;  m2[2][2] = b1;
  return m1 * m2;
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

float Hashff (float p)
{
  return fract (sin (p) * cHashM);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy
vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  float fi;
  fi = float (idVar);
  if (Maxv2 (abs (fCoord - vec2 (mod (fi, txRow), floor (fi / txRow)) - 0.5)) < 0.5) fCol = val;
}

