#define PRACTICLES_COUNT 23.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - vec2(0.5 * iResolution.x, 0.0)) / iResolution.y;
    vec3 color2 = mix(vec3(0.0, 0.0, 0.99), vec3(0.01, 0.0, 0.0), sin(iTime) * (length(uv) - 0.12));

    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));     // Time varying pixel color
    vec3 col = vec3(0)+color2;
    vec3 color = sin(vec3(.34,.54,.43) *floor(iTime)* .5 + .5);
    float t = fract(iTime);
    for( float i =0.0; i < PRACTICLES_COUNT; i++)
    {
        float r = texture( iChannel0, vec2(iTime, 0.0) ).x;
        //vec2 dir = vec2(sin(iTime + 1.0) * .5,rstandard);

        float d = length(uv - i* tan(t));

        float brightness = mix( 0.005, .0001, smoothstep(0.005, 0.0, t) );
        brightness *= sin(t*20.0 + i)* .5 +.5;
        brightness*= smoothstep(1.,.5,r);
        col += brightness/d * color;
    }

    col*= 2.0;
    fragColor = vec4(col,1.0);     // Output to screen
}
