void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 a = texelFetch(iChannel0,ivec2(fragCoord),0);
    uint b = floatBitsToUint(a.x)&255U;
    int i = iFrame&7;
    b = (b>>i)|(b<<(8-i));
    fragColor = cos(float(b&255U)*77.1111+vec4(3,1,2,4))*.5+.5;
}

