    uint m = floatBitsToUint(texelFetch(iChannel0,ivec2(u+vec2( 0,-2)),0).x);
    uint o = 
    +((a>>0U)&1U) +((b>>0U)&1U) +((c>>0U)&1U) +((d>>0U)&1U) +((e>>0U)&1U) +((f>>0U)&1U) +((g>>0U)&1U) +((h>>0U)&1U) +((i>>0U)&1U)   +((j>>0U)&1U) +((k>>0U)&1U) +((l>>0U)&1U) +((m>>0U)&1U)
