    +((a>>4U)&1U) +((b>>4U)&1U) +((c>>4U)&1U) +((d>>4U)&1U) +((e>>4U)&1U) +((f>>4U)&1U) +((g>>4U)&1U) +((h>>4U)&1U) +((i>>4U)&1U)   +((j>>4U)&1U) +((k>>4U)&1U) +((l>>4U)&1U) +((m>>4U)&1U)
    +((a>>5U)&1U) +((b>>5U)&1U) +((c>>5U)&1U) +((d>>5U)&1U) +((e>>5U)&1U) +((f>>5U)&1U) +((g>>5U)&1U) +((h>>5U)&1U) +((i>>5U)&1U)   +((j>>5U)&1U) +((k>>5U)&1U) +((l>>5U)&1U) +((m>>5U)&1U)
    +((a>>6U)&1U) +((b>>6U)&1U) +((c>>6U)&1U) +((d>>6U)&1U) +((e>>6U)&1U) +((f>>6U)&1U) +((g>>6U)&1U) +((h>>6U)&1U) +((i>>6U)&1U)   +((j>>6U)&1U) +((k>>6U)&1U) +((l>>6U)&1U) +((m>>6U)&1U);
    a = (16U*2U+4U)*(16U*8U*16U*8U)+(16U*6U+7U);
    o = (i<<1U) | ((a>>(o&31U))&1U);

