float rand(int i)
{
    return fract(sin(dot(vec2(i,0.) ,vec2(12.9898,78.233))) * 43758.5453);
}

vec4 alphaBlend(vec4 c1, vec4 c2)
{
    return vec4(
        mix(c1.r, c2.r, c2.a),
        mix(c1.g, c2.g, c2.a),
        mix(c1.b, c2.b, c2.a),
        clamp(max(c1.a, c2.a) + c1.a * c2.a, 0., 1.));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    const float thickness = 2.5;
    const int sinCount = 20;
    const int shadowCount = 20;

    const vec3 lineColor = vec3(0.117, 0.564, 1);

    fragCoord -= iResolution.xy / 2.0;
    vec2 uv = fragCoord/iResolution.x*20.0;
    uv = (uv + vec2(500,0)) * vec2(1500, 76);

    float value = 0.;
    float t = iTime / 150.;
    const float mixInfl = 0.0009;
    const float startInfl = 15.;
    for (int i = 0; i < sinCount; i++)
    {
        float rk = mix(rand(int(t) + i) * mixInfl, rand(int(t) + 1 + i) * mixInfl, fract(t));
        value += sin(uv.x * rk * 1.5) * rand(i + 1) * startInfl;
    }
    vec4 col = vec4(0);
    for (int i = -shadowCount; i <= shadowCount; i++)
        col = alphaBlend(col, vec4(lineColor, 0.12 * float(abs(value - uv.y) < thickness * float(i) * 15.)));

    fragColor = vec4(0);
    fragColor = alphaBlend(fragColor, col);
}
