#define pi 3.14159
#define thc(a,b) tanh(a*cos(b))/tanh(a)

vec3 cell(in ivec2 p) {
    ivec2 r = ivec2(textureSize(iChannel0, 0));
    p = (p+r) % r;
    return texelFetch(iChannel0, p, 0 ).rgb;
}

vec3 cell2(in ivec2 p) {
    ivec2 r = ivec2(textureSize(iChannel1, 0));
    p = (p+r) % r;
    return texelFetch(iChannel1, p, 0 ).rgb;
}

float h21 (float a, float b, float r) {
    a = mod(a, r); b = mod(b, r);
    return fract(sin(dot(vec2(a, b), vec2(12.9898, 78.233)))*43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ouv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    fragCoord += 10. * iTime;
    vec2 uv = fragCoord/iResolution.xy;

    float sc = 8.;
    vec2 ipos = floor(sc * uv);
    vec2 fpos = fract(sc * uv);

    float h = h21(ipos.x, ipos.y, 301.);
    vec3 col = cell(ivec2(fpos * 64. + vec2(13, 17) * floor(iTime + 64. * h) * 64.));
   // float v = length(col.gb);   
    vec2 uv2 = col.bg - 0.5;
    float d = length(uv2);
    float k = sc * 2. / iResolution.y;
    float s = step(0.9 + 0.1 * cos(2. * pi * h + iTime), abs(col.r-col.b));
    s += smoothstep(-k, k, -d + 0.45);
    col *= vec3(s);
    col += 0.01;
    fragCoord *= 2. * col.r * 3.;
    vec3 col3 = 4. * cell2(ivec2(fragCoord));
    // forgot uv=col.bg-0.5 when I wrote this (but looks cool)
    col += exp(-2. * length(uv2 - 0.5));
    col /= col3;

    col = clamp(col, 0., 1.);
    float mx = (0.75 + 0.25 * thc(2., iResolution.y * 0.5 * ouv.y+ 0.5 * iTime)) / cosh(2. * length(ouv));
    col = mix(col, 0.7 * col3 * col.r , 1.-mx);
    fragColor = vec4(col,1.0);
}
