#define BEAT (time*138.0/60.0)
#define PI 3.14159265
#define time iTime * 0.75
#define lofi(x,d) (floor((x)/(d))*(d))
#define saturate(a) (clamp((a),0.025,1.))
#define linearstep(a,b,t) (saturate(((t)-(a))/((b)-(a))))

float seed;

float fractsin(float v)
{
  return fract(sin(v*121.445)*34.59);
}

float rand()
{
  seed=fractsin(seed);
  return seed;
}

vec3 randsphere()
{
  float t=PI*2.*rand();
  float p=acos(rand()*2.-1.);
  return vec3(sin(p)*sin(t),cos(p),sin(p)*cos(t));
}

float easeceil(float t, float fac)
{
  return floor(t)+.5+.5*cos(PI*exp(fac*fract(t)));
}

mat2 rot2d(float t)
{
  return mat2(cos(t),-sin(t),sin(t),cos(t));
}

vec3 ifs(vec3 p,vec3 rot,vec3 shift)
{
  vec3 pt=abs(p);
  vec3 t=shift;
  for(int i=0;i<6;i++)
  {
    pt=abs(pt)-abs(lofi(t*pow(1.8,-float(i)),1.0/512.0));
    t.yz=rot2d(rot.x)*t.yz;
    t.zx=rot2d(rot.y)*t.zx;
    t.xy=rot2d(rot.z)*t.xy;
    pt.xy=pt.x<pt.y?pt.yx:pt.xy;
    pt.yz=pt.y<pt.z?pt.zy:pt.yz;
  }
  return pt;
}

float sdbox(vec3 p,vec3 s)
{
  vec3 d=abs(p)-s;
  return length(max(d,0.));
}

// ======= map!!!!!!!!!! ====================================
vec4 map(vec3 p)
{
  vec3 pt=p;
  vec3 haha=lofi(pt,5.0);
  float scrphase=mod(999.9*fractsin(haha.y+haha.z+3.88),PI*2.0);
  float scr=(mod(haha.y+haha.z,2.0)*2.0-1.0)*20.0*smoothstep(-0.5,0.5,sin(time*0.5+scrphase));
  pt.x+=scr;
  haha=lofi(pt,5.0);
  float phase=BEAT/8.0;
  phase+=dot(haha,vec3(2.75,3.625,1.0625));
  phase=easeceil(phase,-10.0);
  pt=mod(pt,5.0)-2.5;
  vec3 pm=pt;
  pt.yz=rot2d(.5*PI*phase+.25*PI)*pt.yz;
  float clampBox=sdbox(pt,vec3(2.25,1.5,1.8));
  pt=ifs(pt,vec3(3.6,3.0+0.4*phase,3.1),vec3(3.0,2.3,3.5));
  pt=mod(pt-.5,1.)-.5;
  float dist=sdbox(pt,vec3(.17));
  dist=max(dist,clampBox);
  return vec4(
    dist,
    sin(PI*fract(phase)),
    step(0.0,0.01-abs(pt.x+pt.y)),
    abs(pm.x)+abs(pm.y)+abs(pm.z)
  );
}

vec3 normalFunc(vec3 p,vec2 d)
{
  return normalize(vec3(
    map(p+d.yxx).x-map(p-d.yxx).x,
    map(p+d.xyx).x-map(p-d.xyx).x,
    map(p+d.xxy).x-map(p-d.xxy).x
  ));
}

float aoFunc(vec3 p,vec3 n)
{
  float accum=0.;
  for(int i=0;i<32;i++){
    vec3 d=(0.02+0.02*float(i))*randsphere();
    d=dot(d,n)<.0?-d:d;
    //accum+=step(map(p+d).x,0.0)/64.0;
    accum+=linearstep(0.02,0.0,map(p+d).x)/64.0;
  }
  return 1.0-sqrt(saturate(6.0*accum));
}

vec2 glitch(vec2 v)
{
  vec2 vt=v;
  for(int i=0;i<6;i++)
  {
    float fac=4.0*pow(2.2,-float(i));
    float s=fractsin(lofi(vt.x,1.6*fac));
    s+=fractsin(lofi(vt.y,0.4*fac));
    s+=fractsin(time);
    float proc=fractsin(s);
    vt+=0.2*step(proc,0.4*exp(-30.0*mod(BEAT,8.0))-0.1)*(vec2(
      fractsin(s+22.56),
      fractsin(s+17.56)
    )-0.5);
  }
  return vt;
}

void mainImage( out vec4 o, in vec2 fragCoord )
{
  vec2 p=(fragCoord.xy*2.0-iResolution.xy)/iResolution.y;
  o=vec4(0,0,0,1);
  seed=texture(iChannel0,p).x;
  seed+=time;

  vec2 po=p;
  p=glitch(p);

  vec3 ro=vec3(4.0*time,0,lofi(BEAT,8.0));
  vec3 rd=vec3(p,-1);
  rd.z+=0.6*length(p);
  float camphase=lofi(BEAT,8.0)+mod(BEAT,8.0)*0.2;
  rd.yz=rot2d(0.33*camphase+0.03*sin(3.0*time))*rd.yz;
  rd.zx=rot2d(0.78*camphase+0.03*cos(3.0*time))*rd.zx;
  rd.xy=rot2d(0.048*camphase)*rd.xy;
  rd=normalize(rd);
  vec3 fp=ro+rd*5.0;
  ro+=0.002*randsphere();
  rd=normalize(fp-ro);

  vec4 dist;
  float rl=0.01;
  float glow=0.0;
  vec3 rp=ro+rl*rd;
  for(int i=0;i<69;i++){ // nice
    dist=map(rp);
    glow=dist.y;
    rl+=dist.x*0.7;
    rp=ro+rl*rd;
  }

  float fog=exp(-0.1*max(0.,rl-5.0));
  o.xyz+=(1.0-fog)*vec3(1.);

  vec3 n2=normalFunc(rp,vec2(0.0,1E-2+4E-2*dist.y));
  vec3 n=normalFunc(rp,vec2(0.0,2E-3));
  float edge=saturate(length(n-n2));
  float gorge=dist.z;

  o.xyz+=fog*0.1*vec3(15.0,1.0,1.5)*glow;

  if(dist.x<1E-3)
  {
    float ao=aoFunc(rp,n);
    o.xyz+=fog*vec3((0.4-0.1*gorge)*ao);
    o.xyz+=fog*edge*dist.y*vec3(15.0,1.0,1.5);
    //o.xyz+=fog*gorge*vec3(2.0,15.0,5.0)*exp(-10.0*mod(time+dist.w,1.0));
  }

  o.xyz+=length(p-po)*2.0*sin(3.0+4.0*o.x+vec3(0.0,2.0,4.0));
  o.xyz=pow(o.xyz,vec3(0.4545));
  o.xyz-=0.2*length(p);
  o.xyz=vec3(
    smoothstep(0.1,0.9,o.x),
    linearstep(0.0,0.8,o.y),
    smoothstep(-0.2,1.1,o.z)
  );
  o.xyz*=1.0+0.1*sin(vec3(0.,1.,2.)+fragCoord.y*2.0);
}

#define PI 3.1415926536

vec2 rotate(vec2 src, float angle) {
    float sn = sin(angle);
    float cs = cos(angle);
    return vec2(src.x * cs - src.y * sn, src.x * sn + src.y * cs);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float radius = 0.0065;
    float angle = iTime * 4.0 + (uv.x * 80.0 + uv.y * 80.0) * 0.015;
    float angle2 = iTime * 4.0 + (uv.x * 80.0 + uv.y * 80.0) * 0.015;
    float angle3 = iTime * 4.0 + (uv.x * 80.0 + uv.y * 80.0) * 0.015;

    vec2 vector = vec2(0, radius);

    vec4 red = texture(iChannel0, uv + rotate(vector/* + sin(iTime * 7.0) * 0.0025*/, angle));
    vec4 green = texture(iChannel0, uv + rotate(vector/* + sin(iTime * 9.0) * 0.0025*/, angle2 + 2.0 * PI / 3.0));
    vec4 blue = texture(iChannel0, uv + rotate(vector/* + sin(iTime * 11.0) * 0.0025*/, angle3 + 2.0 * PI / 3.0 * 2.0));
    fragColor = vec4(red.r, green.g, blue.b, 1.0);

    // Output to screen
    // fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 p = fragCoord.xy;

    vec2 uv = (p.xy) / iResolution.xy;

    //line thickness
    float th = 0.002;

    //gradient detection for r,g and b separatly
    //not sure why I did this actually

    float gxr = texture(iChannel0, uv.xy+vec2(th, 0.)).r
    - texture(iChannel0, uv.xy-vec2(th, 0.)).r ;

    float gyr = texture(iChannel0, uv.xy+vec2(0., th)).r
    - texture(iChannel0, uv.xy-vec2(0., th)).r;

    float gxg = texture(iChannel0, uv.xy+vec2(th, 0.)).g
    - texture(iChannel0, uv.xy-vec2(th, 0.)).g ;

    float gyg = texture(iChannel0, uv.xy+vec2(0., th)).g
    - texture(iChannel0, uv.xy-vec2(0., th)).g;

    float gxb = texture(iChannel0, uv.xy+vec2(th, 0.)).b
    - texture(iChannel0, uv.xy-vec2(th, 0.)).b;

    float gyb = texture(iChannel0, uv.xy+vec2(0., th)).b
    - texture(iChannel0, uv.xy-vec2(0., th)).b;

    //hack to concea noise from: https://www.shadertoy.com/view/Mdf3zr
    float gr = gxr*gxr + gyr*gyr;
    float gg = gxg*gxg + gyg*gyg;
    float gb = gxb*gxb + gyb*gyb;

    //more noise control
    float g = pow((gr+gg+gb)/1.,1.9);


    vec3 col = texture(iChannel1, p / iResolution.xy).xyz;
    col = mix(col,  0.5 + 0.5*cos(iTime*4.+uv.y*6.+vec3(0,2,4)),g*100.);

    fragColor = vec4(col,1.);
}

// This buffer is the feedback loop

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	vec2 uv = fragCoord.xy / iResolution.xy;

    // Convert the uv's to polar coordinates to scale up  
    vec2 polarUv = (uv * 2.0 - 1.0);
    float angle = atan(polarUv.y, polarUv.x);

    // Scale up the length of the vector by a noise function feeded by the angle and length of the vector
    float ll = length(polarUv)*0.4915;

    vec3 base = texture(iChannel0, uv).rgb;

    // Convert the scaled coordinates back to cartesian
    vec2 offs = vec2(cos(angle)*ll + 0.5, sin(angle)*ll + 0.5);

    // sample the last texture with uv's slightly scaled up
    vec3 overlay = texture(iChannel1,offs).rgb;

        // Additively blend the colors together
    vec4 col = vec4(base + (base * overlay*(0.8478975*cos(iTime/5.5))), 1.0);

    fragColor = col;
}
