#define c1 vec3(1.,0.5,1.5)

vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c1.xy)*scale)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col=vec3(0.);  
    float t1 = 36.*16.*16.*2.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    float scale = c1.z;
    float offset = 0.;
    float offset1 = iTime/1000.;
    float p1 = 1.;
    for(int i=0;i<9;i++)
    {
        vec2 t2 = vec2(0.);
        vec2 t3 = vec2(0.);
        for(int k = 0; k < 3; k++){
            uv += (t2.yx);
            vec2 div = 2.5+t2*t3.yx;
            t2 = p1*triangle_wave(uv.yx-.5,scale);
            t3 = -p1*triangle_wave(uv,scale);
            uv.yx = (t2+t3)/div;
            p1 *= -1.;
        }
        col.x = -min(uv.y+uv.x+col.x,col.x);
        //col.x = -max(uv.y+uv.x+col.x,col.x);

        //blue and pink
        col = max(col,abs(col.yzx+vec3(col.x))/2.);

        //orange and blue
        //col = col.yzx/2. + max(col.yzx,abs(col+vec3(col.x))).yzx/2.;

        //pink lace pattern
        //col = max(col,abs(col+vec3(col.x)))/4.;



        uv.y *= -1.;
    }
    fragColor = vec4(col*6.,1.0);
}
