// LOD variant of "Pixel perfect outline" by portponky. https://shadertoy.com/view/7tj3Wh


/* */  // antialiased version
#define inside(P,l) step(.1, length(  textureLod(iChannel0, (P)/iResolution.xy,l)  \
                                    - vec4(.05, .64, .15, 1) ) )
// #define inside(P,l) step(.1, length(  texelFetch(iChannel0, ivec2(P)>>int(l),int(l))  \

void mainImage( out vec4 O, vec2 U )
{
    O -= O;
    for(float l=0.,c; l<8.; l++) {
        c = 0.;
        for (int k=0; k<9; k++)
            c += inside( U + vec2(k%3,k/3)-1. , l);
        if (c<4.) O += .15* vec4(0, .58, .88, 1);
        O += (1.-O) * min( 9.-c , c ) / 5.;
     // O = mix(O, vec4(1), min( 9.-c , c ) / 5. ); // simplified above
    }
}


/*  // --- aliased version 

#define inside(P,l) length( textureLod(iChannel0, (P)/R,l) - vec4(.05, .64, .15, 1) ) > .1

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
     right = vec2(1,0),
      down = vec2(0,1);

    O-=O; 
    for(float l=0.; l<8.; l++)
      if( !(inside(U,l)) ) 
      if (    inside(U + right, l) 
           || inside(U + down , l) 
           || inside(U - right, l) 
           || inside(U - down ,l) ) O++; 
           else O+=.15 * vec4(0, .58, .88, 1);
}

/**/
