// See https://www.desmos.com/calculator/l2bhsgghyw

// SDFs
// https://www.shadertoy.com/view/ft2GRD
vec3 solveQuadratic(in float a, in float b, in float c) {
    if (abs(a) < 1e-6) return vec3(-c / b, 0, 1);
    float h = b * b - 4.0 * a * c;
    if (h < 0.0) return vec3(0.0);
    return vec3((vec2(-1.0, 1.0) * sqrt(h) - b) * 0.5 / a, 2.0);
}

float cbrt(in float x) { return sign(x) * pow(abs(x), 1.0 / 3.0); }
vec4 solveCubic(in float a, in float b, in float c, in float d) {
    float u = b / (3.0 * a);
    vec3 roots;

    float p = (c - b * u) / a;
    float q = (d - (c - 2.0 * b * b / (9.0 * a)) * u) / a;

    if (abs(p) < 1e-9) return vec4(cbrt(-q) - u, 0.0, 0.0, 1);
    float h = 0.25 * q * q + p * p * p / 27.0;
    if (h > 0.0) {
        h = sqrt(h);
        float o = -0.5 * q;
        roots.x = cbrt(o - h) + cbrt(o + h) - u;
        return vec4(roots, 1);
    }

    float m = sqrt(-p / 3.0);
    roots.x = -2.0 * m * sin(asin(1.5 * q / (p * m)) / 3.0);

    h = sqrt(-3.0 * roots.x * roots.x - 4.0 * p);
    roots.yz = 0.5 * vec2(h - roots.x, -h - roots.x);
    roots -= u;

    return vec4(roots, 3);
}

float evalDist(in vec2 p, in float t, in vec2 a, in vec2 b, in vec2 c, in vec2 d) {
    t = clamp(t, 0.0, 1.0);
    vec2 toCurve = p - (((a * t + b) * t + c) * t + d);
    return max(abs(toCurve.x), abs(toCurve.y));
}

float sdCubicCurve(in vec2 p, in vec2 a, in vec2 b, in vec2 c, in vec2 d) {
    vec2 dp = d - p;

    vec4 minMax1 = solveCubic(a.x - a.y, b.x - b.y, c.x - c.y, dp.x - dp.y);
    vec4 minMax2 = solveCubic(a.x + a.y, b.x + b.y, c.x + c.y, dp.x + dp.y);

    vec3 minMax3 = solveQuadratic(3.0 * a.x, 2.0 * b.x, c.x);
    vec3 minMax4 = solveQuadratic(3.0 * a.y, 2.0 * b.y, c.y);

    float dist = evalDist(p, minMax1[0], a, b, c, d);
    for (int n=1; n < int(minMax1.w); n++) dist = min(dist, evalDist(p, minMax1[n], a, b, c, d));
    for (int n=0; n < int(minMax2.w); n++) dist = min(dist, evalDist(p, minMax2[n], a, b, c, d));
    for (int n=0; n < int(minMax3.z); n++) dist = min(dist, evalDist(p, minMax3[n], a, b, c, d));
    for (int n=0; n < int(minMax4.z); n++) dist = min(dist, evalDist(p, minMax4[n], a, b, c, d));

    return dist;
}

#define drawSDF(dist, col) color = mix(color, col, smoothstep(unit, 0.0, dist))
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    vec3 color = vec3(0.0);

    float ang = texelFetch(iChannel0, ivec2(0), 0).x;
    float co = cos(ang), si = sin(ang);
    mat2 rot = mat2(co, si, -si, co);

    float curve = sdCubicCurve(rot * uv, T3, T2, T1, T0);
    drawSDF(curve - 0.01, vec3(1.0, 0.0, 0.0));
    drawSDF(length(uv) - 0.05, vec3(1.0, 0.8, 0.0));

    float aspect = iResolution.x / iResolution.y;
    color = digitIn(color, vec3(1.0), uv * 0.25 + vec2(0.45 * aspect, 0.45), 100.0, cubicCurveInertia(T3, T2, T1, T0));

    fragColor = vec4(color, 1.0);
}

// Constants
#define PI 3.14159265359

// Curve settings
#define T3 (vec2(2.759 + (iTime > 13.0 ? 0.1 * sin((iTime - 13.0) * 6.0) : 0.0), 0.379) * 3.0)
#define T2 (vec2(-4.872, -2.151) * 3.0)
#define T1 (vec2(2.487, 1.755) * 3.0)
#define T0 vec2(0.0, 0.0)
#define MASS 0.1

// Simulation settings
#define FRICTION 0.98
#define GRAVITY 0.2

// cross3D(vec3(a.x, a.y, 0), vec3(b.x, b.y, 0))
// Since the x and y components of the result are always zero
// The z component is the only one of interest
float cross2D(in vec2 a, in vec2 b) {
    return a.x * b.y - a.y * b.x;
}

// Moment of inertia of a cubic curve with coefficients t3, t2, t1, and t0
float cubicCurveInertia(in vec2 t3, in vec2 t2, in vec2 t1, in vec2 t0) {
    return dot(t3, t3) / 7.0 + (dot(t2, t2) + 2.0 * dot(t1, t3)) / 5.0 +
          (dot(t1, t1) + 2.0 * dot(t0, t2) + dot(t2, t3)) / 3.0 +
          (dot(t1, t2) + dot(t0, t3)) / 2.0 + dot(t0, t1) + dot(t0, t0);
}

