const int CHARACTERS[14] = int[14](31599, 9362, 31183, 31207, 23524, 29671, 29679, 30994, 31727, 31719, 1488, 448, 2, 3640);

float digitIsOn(in int digit, in vec2 id) {   
    if (id.x < 0.0 || id.y < 0.0 || id.x > 2.0 || id.y > 4.0 ) return 0.0;
    return floor(mod(float(CHARACTERS[int(digit)]) / pow(2.0, id.x + id.y * 3.0), 2.0));
}

float digitSign(in float v, in vec2 id) {
    return digitIsOn(10 - int((sign(v) - 1.0) * 0.5), id);
}

int digitCount(in float v) {
    return int(floor(log(max(v, 1.0) ) / log(10.0)));
}

float digitFirst(in vec2 uv, in float scale, in float v, in int decimalPlaces) {
    vec2 id = floor(uv * scale);

    if (0.0 < digitSign(v, id)) return 1.0;
    v = abs(v);

    int digits = digitCount(v);
    float power = pow(10.0, float(digits));

    float offset = floor(0.1 * scale);
    id.x -= offset;

    float n;
    for (int i = 0; i < 33; i++, id.x -= offset, v -= power * n, power /= 10.0) {
        n = floor(v / power);
        if (0.0 < digitIsOn(int(n), id)) return 1.0;   
        if (i == digits) {
            id.x -= offset;
            if (0.0 < digitIsOn(int(12), id)) return 1.0;
        }

        if (i >= digits + decimalPlaces) return 0.0;
    }

    return 0.0;
}

float digitFirst(in vec2 uv, in float scale, in float v) {
    return digitFirst(uv, scale, v, 3);
}

vec3 digitIn(in vec3 color, in vec3 toMix, in vec2 uv, in float scale, in float v) {
    float f = digitFirst(uv, scale, v);
    return mix(color, toMix, f);
}

void mainImage(out vec4 data, in vec2 addr) {
    data = vec4(-PI / 4.0, 0.0, 0.0, 0.0);
    addr -= 0.5;
    ivec2 iAddr = ivec2(addr);
    if (iFrame > 0 && iAddr == ivec2(0)) {
        data = texelFetch(iChannel0, ivec2(0), 0);

        float ang = texelFetch(iChannel0, ivec2(0), 0).x;
        float co = cos(ang), si = sin(ang);
        mat2 rot = mat2(co, si, -si, co);

        vec2 centerOfMass = (T3 / 4.0 + T2 / 3.0 + T1 / 2.0 + T0) * rot;

        vec2 force = vec2(0.0, -MASS * GRAVITY);
        float torque = cross2D(-centerOfMass, force);

        data.y += torque / cubicCurveInertia(T3, T2, T1, T0);
        data.y *= FRICTION;
        data.x += data.y;
    }
}
