/*
  No. 38 in "Ball Dynamics" series; others are listed in "Balls Falling" (NsKGR3)
*/

#define AA  0  // (= 0/1) optional antialiasing

#if 1
#define VAR_ZERO min (iFrame, 0)
#else
#define VAR_ZERO 0
#endif

float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrSphDf (vec3 p, float r);
float PrTorusDf (vec3 p, float ri, float rc);
vec3 OrAng (vec3 p);
vec3 OrAngT (vec3 p);
float Minv3 (vec3 p);
float Maxv3 (vec3 p);
mat3 StdVuMat (float el, float az);
mat3 QtToRMat (vec4 q);
vec2 Rot2D (vec2 q, float a);
vec2 Rot2Cs (vec2 q, vec2 cs);
vec3 HsvToRgb (vec3 c);
float Fbm2 (vec2 p);
vec4 Loadv4 (int idVar);

const ivec3 nbEdge = ivec3 (8, 2, 8);  // same in all buffs
const int nBall = nbEdge.x * nbEdge.y * nbEdge.z;

vec3 ltDir, vnBall, hbSize, vnBlk;
vec2 blkAng;
vec2 qTube, aRotCs[2];
float tCur, dstFar, hbMarg, radBs, radUp, hUp;
int idObj, idBall;
const int txOff = 2;
const float pi = 3.1415927;

const int nSeg = 3;
const int nTube = 4;

struct TbCon {
  vec3 pLo, pHi;
  vec2 sRotCs[2], tRotCs[2];
  float chLen, segRad;
};
TbCon tbCon[nTube];

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

#define F(x) (sin (x) / x - b)

float TubesDf (vec3 p, float dMin)
{ // (modded from "Elevating Platter" and "Slithering Worm")
  vec3 q, pp;
  vec2 b;
  float tubeRad, d, s;
  tubeRad = 0.2;
  q = p;
  q.y -= tbCon[0].pLo.y;
  d = PrTorusDf (q.xzy, 0.5 * tubeRad, radBs);
  q.y -= hUp - tbCon[0].pLo.y;
  q.xy = Rot2Cs (q.xy, aRotCs[0]);
  q.zy = Rot2Cs (q.zy, aRotCs[1]);
  d = min (d, PrTorusDf (q.xzy, 0.5 * tubeRad, radUp));
  DMIN (2);
  pp = p;
  for (int k = VAR_ZERO; k < nTube; k ++) {
    p = pp - tbCon[k].pLo;
    p.xz = Rot2Cs (p.xz, tbCon[k].tRotCs[0]);
    p.xy = Rot2Cs (p.xy, tbCon[k].tRotCs[1]);
    p.xy = Rot2Cs (p.xy, tbCon[k].sRotCs[1]);
    q = p;
    d = PrSphDf (q, 1.2 * tubeRad);
    DMIN (2);
    s = 1.;
    for (int j = 0; j < nSeg; j ++) {
      q = p;
      q.xy = Rot2Cs (vec2 (- q.y, q.x - tbCon[k].segRad), tbCon[k].sRotCs[0]);
      b = vec2 (length (q.xy) - tbCon[k].segRad, q.z);
      d = max (length (b) - tubeRad, dot (vec2 (abs (q.x), q.y), tbCon[k].sRotCs[0]));
      p.xy = Rot2Cs (vec2 (q.y, - q.x), tbCon[k].sRotCs[0]);
      p.x = - (p.x + tbCon[k].segRad);
      s = - s;
      if (d < dMin) qTube = atan (vec2 (- q.x, b.x), vec2 (- q.y, b.y)) * vec2 (tbCon[k].segRad, s);
      DMIN (3);
    }
    q = p;
    d = PrSphDf (q, 1.2 * tubeRad);
    DMIN (2);
  }
  return dMin;
}

float SecSolve (float b)
{  // (from "Bucking Bronco")
  vec3 t;
  vec2 f;
  float x;
  if (b < 0.95) {
    t.yz = vec2 (0.7, 1.2);
    f = vec2 (F(t.y), F(t.z));
    for (int nIt = 0; nIt < 4; nIt ++) {
      t.x = (t.z * f.x - t.y * f.y) / (f.x - f.y);
      t.zy = t.yx;
      f = vec2 (F(t.x), f.x);
    }
    x = t.x;
  } else if (b < 1.) {
    x = sqrt (10. * (1. - sqrt (1. - 1.2 * (1. - b))));
  } else {
    x = 0.;
  }
  return x;
}

void SetConf ()
{
  vec3 vp, s;
  float segLen, segAng, a, aRot[2];
  segLen = 2.;
  hUp = 6.;
  radUp = sqrt(2.) * (hbSize.x - hbMarg) + 0.1;
  radBs = 1.3 * radUp;
  aRot[0] = - blkAng.y;
  aRot[1] = - blkAng.x;
  aRotCs[0] = sin (aRot[0] + vec2 (0.5 * pi, 0.));
  aRotCs[1] = sin (aRot[1] + vec2 (0.5 * pi, 0.));
  for (int k = 0; k < nTube; k ++) {
    a = 2. * pi * float (k) / float (nTube);
    tbCon[k].pLo.y = 0.2;
    tbCon[k].pLo.xz = Rot2D (vec2 (radBs, 0.), a);
    tbCon[k].pHi.y = hUp;
    s.xz = Rot2D (vec2 (radUp, 0.), a);
    s.xy = Rot2D (vec2 (s.x, 0.), - aRot[0]);
    s.zy = Rot2D (s.zy, - aRot[1]);
    tbCon[k].pHi = vec3 (0., hUp, 0.) + s;
    vp = tbCon[k].pHi - tbCon[k].pLo;
    tbCon[k].chLen = 0.5 * length (vp) / float (nSeg);
    segAng = max (1e-4, SecSolve (tbCon[k].chLen / segLen));
    tbCon[k].segRad = segLen / segAng;
    tbCon[k].sRotCs[0] = sin (segAng + vec2 (0.5 * pi, 0.));
    tbCon[k].sRotCs[1] = sin (- segAng + vec2 (0.5 * pi, 0.));
    tbCon[k].tRotCs[0] = sin (atan (vp.z, - vp.x) + pi + vec2 (0.5 * pi, 0.));
    tbCon[k].tRotCs[1] = sin (asin (length (vp.xz) / length (vp)) + vec2 (0.5 * pi, 0.));
  }
}

float ObjDf (vec3 p)
{
  vec4 sLen;
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  q = OrAng (p - vec3 (0., hUp, 0.));
  q = abs (q);
  sLen = vec4 (hbSize - hbMarg - 0.05, 0.);
  d = min (min (
     PrRoundBoxDf (q - sLen.wyz, sLen.xww + 0.05, 0.05),
     PrRoundBoxDf (q - sLen.xwz, sLen.wyw + 0.05, 0.05)),
     PrRoundBoxDf (q - sLen.xyw, sLen.wwz + 0.05, 0.05));
  DMIN (1);
  dMin = TubesDf (p, dMin);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    p = ro + dHit * rd;
    d = ObjDf (p);
    dHit += d;
    if (d < 0.001 || dHit > dstFar || p.y < 0.) break;
  }
  if (p.y < 0.) dHit = dstFar;
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.0001, -0.0001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float BallHit (vec3 ro, vec3 rd)
{
  vec4 p;
  vec3 u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (txOff + 4 * n);
    u = ro - p.xyz;
    rad = 0.49 * p.w;
    b = dot (rd, u);
    w = b * b - dot (u, u) + rad * rad;
    if (w > 0.) {
      d = - b - sqrt (w);
      if (d > 0. && d < dMin) {
        dMin = d;
        vnBall = (u + d * rd) / rad;
        idBall = n;
      }
    }
  }
  return dMin;
} 

float BallHitSh (vec3 ro, vec3 rd, float rng)
{
  vec4 p;
  vec3 u;
  float b, d, w, dMin, rad;
  dMin = dstFar;
  for (int n = VAR_ZERO; n < nBall; n ++) {
    p = Loadv4 (txOff + 4 * n);
    u = ro - p.xyz;
    rad = 0.49 * p.w;
    b = dot (rd, u);
    w = b * b - dot (u, u) + rad * rad;
    if (w > 0.) {
      d = - b - sqrt (w);
      if (d > 0. && d < dMin) dMin = d;
    }
  }
  return smoothstep (0., rng, dMin);
}

float BallChqr (int idBall, vec3 vnBall)
{
  vec3 u;
  vec2 a;
  u = vnBall * QtToRMat (Loadv4 (txOff + 4 * idBall + 2));
  a = mod (floor (8. * vec2 (atan (u.x, u.y), asin (u.z)) / pi), 2.) - 0.5;
  return step (0., sign (a.x) * sign (a.y));
}

float BlkHit (vec3 ro, vec3 rd)
{
  vec3 v, tm, tp, u;
  float dMin, dn, df;
  dMin = dstFar;
  v = ro / rd;
  tp = (hbSize - hbMarg - 0.04) / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) {
    dMin = dn;
    vnBlk = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  }
  return dMin;
}

float ObjSShadow (vec3 ro, vec3 rd, float dMax)
{
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.04 * d, h));
    d += h;
    if (sh < 0.001 || d > dMax) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, rob, rdb, vn;
  vec2 u;
  float dstBall, dstObj, db, nDotL, sh;
  SetConf ();
  col = vec3 (0.);
  rob = OrAng (ro - vec3 (0., hUp, 0.));
  rdb = OrAng (rd);
  db = BlkHit (rob, rdb);
  dstBall = (db < dstFar) ? BallHit (rob, rdb) : dstFar;
  dstObj = ObjRay (ro, rd);
  if (min (dstObj, dstBall) < dstFar) {
    if (dstObj < dstBall) {
      ro += dstObj * rd;
      vn = ObjNf (ro);
      if (idObj == 1) col4 = vec4 (0.8, 0.8, 0.85, 0.1);
      else if (idObj == 2) col4 = vec4 (0.8, 0.9, 0.8, 0.1);
      else if (idObj == 3) col4 = mix (vec4 (1., 0.7, 0., 0.2), vec4 (0.6, 0.6, 1., 0.2),
         step (0., sin (8. * pi * qTube.x + 3. * qTube.y)));
    } else {
      ro += dstBall * rd;
      vn = OrAngT (vnBall);
      col4 = vec4 (HsvToRgb (vec3 (fract (33. * float (idBall) / float (nBall) +
         0.5 * BallChqr (idBall, vn)), 0.9, 1.)), 0.3);
    }
    nDotL = max (dot (vn, ltDir), 0.);
    nDotL *= nDotL;
  } else if (rd.y < 0.) {
    dstObj = - ro.y / rd.y;
    ro += dstObj * rd;
    vn = vec3 (0., 1., 0.);
    nDotL = max (dot (vn, ltDir), 0.);
    u = 0.25 * ro.xz;
    col4 = vec4 (0.6, 0.6, 0.7, 0.1) * (1. - 0.2 * Fbm2 (4. * u));
    u = abs (fract (u + 0.5) - 0.5);
    col4.rgb *= (1. - 0.15 * smoothstep (0.05, 0.08, abs (max (abs (u.x + u.y), abs (u.x - u.y)) -
       0.2) - 0.2)) * (1. - 0.15 * smoothstep (0.05, 0.08, length (max (u - 0.42, 0.))));
  }
  if (min (dstObj, dstBall) < dstFar) {
    sh = (dstObj < dstFar) ? ObjSShadow (ro, ltDir, dstFar) : 1.;
    sh = min (sh, 0.5 + 0.5 * BallHitSh (OrAng (ro - vec3 (0., hUp, 0.)), OrAng (ltDir), 20.));
    col = col4.rgb * (0.2 + 0.8 * sh * nDotL) + col4.a * step (0.95, sh) *
       pow (max (dot (ltDir, reflect (rd, vn)), 0.), 32.);
    col *= 1. - 0.9 * smoothstep (0.2, 0.3, length (ro.xz) / dstFar);
  }
  if (db < min (dstObj, dstFar)) {
    col *= 0.95;
    vn = OrAngT (vnBlk);
    col = mix (col, vec3 (0.7, 0.7, 0.75) * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
       0.1 * pow (max (dot (ltDir, reflect (rd, vn)), 0.), 32.), pow (1. - abs (dot (rd, vn)), 5.));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr, stDat;
  vec3 ro, rd, col;
  vec2 canvas, uv, uvv;
  float el, az, asp, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  asp = canvas.x / canvas.y;
  az = 0.1 * pi;
  el = -0.1 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az += 0.005 * pi * tCur;
  }
  el = clamp (el, -0.35 * pi, -0.02 * pi);
  stDat = Loadv4 (0);
  hbSize = stDat.xyz;
  stDat = Loadv4 (1);
  blkAng = stDat.xy;
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 3., -40.);
  hbMarg = 0.4;
  zmFac = 4.;
  dstFar = 20. * length (hbSize);
  ltDir = vuMat * normalize (vec3 (-1., 1., -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr *
       (0.667 * a + 0.5) * pi)) / zmFac;
    rd = vuMat * normalize (vec3 ((2. * tan (0.5 * atan (uvv.x / asp))) * asp, uvv.y, 1.));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

mat3 QtToRMat (vec4 q) 
{
  mat3 m;
  vec3 a1, a2;
  a1 = q.yzx * q.zxy;
  a2 = q.w * q.xyz;
  a1 += a2;
  a2 = a1 - 2. * a2;
  m[1][2] = a1.x;  m[2][1] = a2.x;
  m[2][0] = a1.y;  m[0][2] = a2.y;
  m[0][1] = a1.z;  m[1][0] = a2.z;
  q.xyz = q.xyz * q.xyz + q.w * q.w - 0.5;
  m[0][0] = q.x;  m[1][1] = q.y;  m[2][2] = q.z;
  return 2. * m;
}

vec3 OrAng (vec3 p)
{
  p.yx = Rot2D (p.yx, blkAng.y);
  p.yz = Rot2D (p.yz, blkAng.x);
  return p;
}

vec3 OrAngT (vec3 p)
{
  p.yz = Rot2D (p.yz, - blkAng.x);
  p.yx = Rot2D (p.yx, - blkAng.y);
  return p;
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec3 HsvToRgb (vec3 c)
{
  return c.z * mix (vec3 (1.), clamp (abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. -
     3.) - 1., 0., 1.), c.y);
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

