#define C(U) texture(iChannel2,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U) {R = iResolution.xy;T = iTime;
#define ei(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define pi 3.14159265359
#define _sin(a) sin(mod(a,2.*pi))
#define _cos(a) cos(mod(a,2.*pi))


float ln (vec3 p, vec3 a, vec3 b) {
    float l = clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.);
    return mix(.7,1.,l)*length(p-a-(b-a)*l);
}
vec4 map (vec3 u) {

    u.xz *= ei(.9);
    u.xy *= ei(1.5);
    float d = 1e9;
    vec4 c=vec4(0);
    float sg = 1e9;
    float l = .1;
    u.y = abs(u.y);
    u.y+=.1;
    mat2 M1 = ei(2.);
    mat2 M2 = ei(.4);
    float w = 0.05;
    for (float i = 0.; i < 18.; i++)
    {
        sg = ln(u,vec3(0),vec3(0,l,0))/l;
        d = min(d,sg*l-w);
        w *= .66;
        u.y -= l;
        u.xz *= M1;
        u.xz = abs(u.xz);
        u.xy *= M2;
        l *= .75;
        c += exp(-sg*sg)*(.5+.5*sin(3.1*i/16.+vec4(1,2,3,4)));
    }
    return vec4(c.xyz,d);
}
