#define pi 3.14159

#define MAX_STEPS 50
#define MAX_DIST 50.
#define SURF_DIST .001

#define S smoothstep
#define T iTime

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
} 

float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}


float h21 (vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

float mlength(vec3 p) {
    return max(max(abs(p.x), abs(p.y)), abs(p.z));
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float GetDist(vec3 p) {
    float sc = 4.;
    float sc2 = 2.;

    float a = atan(p.z, p.x);
    float th = atan(p.y, p.x);
    float r = length(p);/// (1. + p.y);

    float tim = 1. * a + r * 4. - iTime;
    tim += 0.4 * h21(p.xz);
    p = 1.2 * r * vec3(sin(a + tim) * cos(th+ tim), cos(a - tim) * sin(th+ tim), cos(th + tim) + sin(th + tim));

    vec3 ip = floor(sc2 * p) + 0.;
    vec3 fp = fract(sc2 * p) - 0.5;


    vec3 vec = 0.3 * vec3(cos(10. * h21(ip.xy) + tim), cos(10. * h21(ip.yz) + tim), cos(10. * h21(ip.zx) + tim));
    float d0 = length(p) - 4. * mlength(fp) - 0.;
    //d0 = length(p) - 1.;

    //p.y += cos(0.4 * r);
   // p = vec3(a / pi, p.y, -0.4 * log(r) + .2 * iTime);

    vec2 ipos = floor(sc * p.xz) + 0.5;
    vec2 fpos = fract(sc * p.xz) - 0.5; //cos(sc * p.xz / pi) - 0.5;

    return 0.23 * d0;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

vec3 Bg(vec3 rd) {
    float k = rd.y *.5 + .5;

    vec3 col = mix(vec3(.5,0.,0.),vec3(0.),k);
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    float time = 0.2 * iTime;

    float height = 0.;//1. + cos(time);
    float dist = 5.;
    vec3 ro = vec3(dist * cos(time), height, dist * sin(time));
    //ro.yz *= Rot(-m.y*3.14+1.);
    //ro.xz *= Rot(-m.x*6.2831);

    vec3 rd = GetRayDir(uv, ro, vec3(0,height,0), 1.);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd);   

    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);
       // vec3 rf = refract(rd, n,0.1);

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        col = 0.5 * vec3(dif);

        float b = .5 + .5 * cos(iTime);

        uv = fragCoord.xy / iResolution.xy;
        col += 0.5 * texture(iChannel0, r).rgb;

       // float a = atan(rf.z, rf.x);
       // float c = atan(rf.z,rf.y);
        vec2 ipos = floor(p.xz) + 0.5;
        float h = mix(h21(ipos),0., 0.5 + 0.5 * cos(iTime));
        //col.r += 0.15 + .3 * cos(10. * rf.y + iTime - .5 * pi);
        //col.g += 0.15 + .3 * cos(10. * rf.y + iTime);
        //col.b += 0.15 + .3 * cos(10. * rf.y + iTime + .5 * pi);
        //col = min(col, 1.2 * vec3(0.5 + 0.5 * cos(2. * p.y)));
       // col *= clamp(1.05 * p.y, 0., 1.);
        //col *= 0.5 + 0.5 * cos(length(p) + iTime);
        col += thc(2., 2.5 * length(p) - iTime);

        vec3 e = vec3(1.);
        col = col * pal(2.5 * length(p) - iTime, e, e, e, 0.25 * vec3(0.,0.33,0.66));
       // col *= n.y;
    } else {
       // col = texture(iChannel0, rd).rgb;
    }

    //col.r =0.02;

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}
