float julia_set(vec2 z, vec2 c)
{
    //int MAX_ITER = int(sin(iTime)*49.0+51.0);
    int MAX_ITER = 195;
    int i = 0;
    for (; i < MAX_ITER; i++)
    {
        z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + c;
        if (length(z) > 2.0)
            break;
    }
    return float(i) / float(MAX_ITER);
}

vec3 get_color(float v)
{
    v *= 3.0;
    return vec3(
        smoothstep(0.0, 1.0, v),
        smoothstep(1.0, 2.0, v), 
        smoothstep(2.0, 3.0, v));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2.0*fragCoord.xy - iResolution.xy) / iResolution.x;
    uv *= 2.1;
    uv *= 0.08;
    uv.y -= 0.12;

    float c = julia_set(vec2(abs(uv.x), uv.y), vec2(0.375));
    vec3 color = get_color(c);
    fragColor = vec4(color.rgb, 1.0);
}
