void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.x;

    vec2 wave = vec2(uv.x, sin(iTime * 2. + uv.x * 4.) * 0.1 + uv.y) * 10.;
    vec2 guv = fract(wave);
    vec2 gid = floor(wave - .5);

    // Time varying pixel color
    vec3 col = vec3(0);
    if (length(guv - 0.5)>0.3) {
        col = 0.5 + 0.5*cos(iTime+uv.xyx+gid.xyx+vec3(0,2,4));
    } else {
        if (length(guv - 0.5) < 0.3 * smoothstep(.5, 0., abs(sin(iTime + uv.x)))) {
            col = vec3(1.);
        }
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
