// variant of tripole https://shadertoy.com/view/Ns23zR
// variant of dipole  https://shadertoy.com/view/Nsj3zR


#define F(P)  1./dot(P-U,P-U)       // point field
#define CS(a) vec2(cos(a),sin(a))

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( 2.*u - R ) / R.y,
         M = ( 2.*iMouse.xy  - R ) / R.y,
         P = vec2(.4,0) +.3*CS(2.*iTime);
    vec4 f = vec4( F(vec2(0,.3)), F(M), F(vec2(0,-.3)), F(P) );

#if 1 // --- the correct way: using a simplex
    vec2 A = CS(1.9111),  // 109.5Â° : tetraedron vertical angle
         j = CS(2.0944);  // 2pi/3 

    mat4x3 J = mat4x3( vec3(0,0,1) ,vec3(1,0,1)*A.yyx, vec3(j,1)*A.yyx, vec3(j.x,-j.y,1)*A.yyx );
    vec3 F;
#else // --- the wrong way: using 2D equals angle ( because not all mutually equal ) 
    mat4x2 J = mat4x2( vec2(1,0) ,vec2(0,1), vec2(-1,0), vec2 (0,-1) );
    vec2 F;
#endif    

    F = J * f;
    O = .5+.5*  F * J; O += (O.a-.5)*vec4(1,1,0,0);

    f /= R.y;
    O =  max( O, max( max( f.x, f.y ),  max( f.z , f.w ) ));
}
