#define MAX_STEPS 100
#define MAX_DIST 100.
#define MIN_DIST 0.001
#define EPSILON 0.001
#define Ka 0.5
#define Kd 0.4
vec3 GetColorYMaterial(vec3 p,  vec3 n, vec3 ro,  vec3 rd, int id_color, float id_material);
vec3 getMaterial( vec3 pp, float id_material);
vec3 light_pos1;  vec3 light_color1 ;
vec3 light_pos2;  vec3 light_color2 ;

//operacion de Union  por FabriceNeyret2
#define opU2(d1, d2) ( d1.x < d2.x ? d1 : d2 )

float sdSphere( vec3 p, float s )
	{ return length(p)-s;}
float sdBox( vec3 p, vec3 b )
	{ vec3 d = abs(p) - b;   return length(max(d,0.0))+ min(max(d.x,max(d.y,d.z)),0.0); }

float sdCircle( vec2 p, float r )
{ return length(p) - r;}

float sdBox( in vec2 p, in vec2 b )
{ vec2 d = abs(p)-b;  return length(max(d,0.0)) + min(max(d.x,d.y),0.0);  }


float Intersect(float distA, float distB)
	{ return max(distA, distB);}
float Union(float distA, float distB)
	{ return min(distA, distB);}
float Difference(float distA, float distB)
	{ return max(distA, -distB);}

float opRep1D( float p, float c )
	{ float q = mod(p+0.5*c,c)-0.5*c; return  q ;}

vec3 rotate_x(vec3 p, float phi)
{   float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 rotate_y(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}
vec3 rotate_z(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}

float yin1(vec2 p)
{
    float d0a=sdCircle(p,4.0);
    float d0b= sdBox(p-vec2(0.0,2.0), vec2(4.0,2.2) );
    float d0c= sdBox(p-vec2(0.0,-2.0), vec2(4.0,2.2) );

    float d1a=sdCircle(p-vec2(-2.0,0.0),2.1); //circulo pequeño izq
    float d1b=sdCircle(p-vec2( 2.0,0.0),2.1); //circulo pequeño izq

    float difci1=Difference(d0a, d0b);
    float difci2=Difference(d0a, d0c);

    float duni1= Union(difci1, d1a);
    float duni2= Union(difci2, d1b);

    //restar otro circulo
    duni1= Difference(difci1, d1b);
    // unir el otro cirulo por borrar
    duni1= Union(duni1, d1a);
    return duni1;

}

float yin2(vec2 p)
{   float d1a=sdCircle(p-vec2(-2.0,0.0),0.5); //circulo pequeño izq
    return d1a;    
}


float yang1(vec2 p)
{
    float d0a=sdCircle(p,4.0);
    float d0b= sdBox(p-vec2(0.0,2.1), vec2(4.0,2.1) );
    float d0c= sdBox(p-vec2(0.0,-2.2), vec2(4.0,2.1) );

    float d1a=sdCircle(p-vec2(-2.0,0.0),2.0); //circulo pequeño izq
    float d1b=sdCircle(p-vec2( 2.0,0.0),2.0); //circulo pequeño izq

    float difci2=Difference(d0a, d0c);
   float duni2= Union(difci2, d1b);

    ///-------------segundo circulo
    duni2=Difference(duni2, d1a);
    return duni2;
}


float yang2(vec2 p)
{   float d1b=sdCircle(p-vec2( 2.0,0.0),0.5); //circulo pequeño izq
    return d1b;
}


vec2 GetDist1(vec3 p  )
{	vec2 res= vec2(9999.0, -1.0); 
    float t=iTime;
    float pzm1=2.0*sin(t);
    float pzm2=2.0*cos(t);

    float d1a= yin1(p.xy)-0.05;
    float d1b= sdBox( p, vec3(10.0,6.0,3.0+pzm1) );

    float d2a= yang1(p.xy)-0.05;
    float d2b= sdBox( p, vec3(10.0,6.0,3.0)+pzm2 );

    float d3a= yin2(p.xy);
    float d3b= sdBox( p, vec3(5.0,5.0,4.0)+pzm1 );

    float d4a= yang2(p.xy);
    float d4b= sdBox( p, vec3(5.0,5.0,4.0)+pzm2 );

    float dunia= Intersect(d1a, d1b);
    float dunib= Intersect(d2a, d2b);
    float dunic= Intersect(d3a, d3b);
    float dunid= Intersect(d4a, d4b);

    res =opU2(res, vec2(dunia,32.0));
    res =opU2(res, vec2(dunib,33.0));
    res =opU2(res, vec2(dunic,33.0));
    res =opU2(res, vec2(dunid,32.0));
    return res;
}

vec2  opScaleGetDis( vec3 p,float s)
{   vec2 res =GetDist1(p/s);
    res.x =res.x*s;
    return res;
}


vec2 GetDist(vec3 p  ) 
{	vec2 res= vec2(9999.0, -1.0); 
    vec3 p0=p;
    float escala1=5.0*sin(iTime*0.25);
    float escala2=-5.0*sin(iTime*0.25);

    vec2 gt1;
    if (escala1>0.0)
        gt1= opScaleGetDis(p, escala1);
    else
        gt1= opScaleGetDis(p, escala2);
    res =opU2(res, gt1);
    return res;
}

vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

vec2 RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   vec3 p;
    vec2 hit, object=vec2(0.1,0.0);
    for(int i=0; i <= PMaxSteps; i++) 
    { p = ro + rd*object.x;
      hit = GetDist(p);
      object.x += hit.x;
      object.y = hit.y;
      if (abs(hit.x) < EPSILON || object.x > MAX_DIST) break;
    }    
    return object;
}


float getSoftShadow(vec3 p, vec3 lightPos) {
    float res = 9999.0;
    float dist = 0.01;
    float lightSize = 0.03;
    for (int i = 0; i < MAX_STEPS; i++) {
      float hit = GetDist(p + lightPos * dist).x;
      res = min(res, hit / (dist * lightSize));
      dist += hit;
      if (hit < 0.0001 || dist > 60.0) break;
    }
    return clamp(res, 0.0, 1.0);
}

float occlusion(vec3 pos, vec3 nor)
{   float sca = 2.0, occ = 0.0;
    for(int i = 0; i < 10; i++) {    
      float hr = 0.01 + float(i) * 0.5 / 4.0;
      float dd = GetDist(nor * hr + pos).x;
      occ += (hr - dd)*sca;
      sca *= 0.6;
    }
    return clamp( 1.0 - occ, 0.0, 1.0 );    
}


vec3 pallete(float t) {
    vec3 a = vec3(0.2);
    vec3 b = vec3(0.5);
    vec3 c = vec3(1.0);
    vec3 d = vec3(0.0, 0.33, 0.67);
    return a + b * cos(6.28318 * (c*t + d));
}

vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro,vec3 lightColor, float t) 
{   vec3 lightPos=lp;
    vec3 worldPos = p;
    vec3 V = -rd;
    vec3 N = normal;
    vec3 L = normalize (lightPos - worldPos);
    vec3 R = reflect (-L, N);

    float lightDist = max(length(L), .001);
    float atten=1.0 / (1.0 + lightDist * 0.125 + lightDist * lightDist * .05);
    L /= (lightDist*atten);

    float shadow = getSoftShadow(worldPos, L);// shadows

    float occ = occlusion(worldPos, N);// occ
    vec3 ambient = Ka + Ka * dot(normal, vec3(0., 1., 0.))*lightColor;
    ambient*=0.5;

    vec3 fresnel =  lightColor *  pow(clamp(1.0 + dot(rd, N), 0.0, 1.0), 2.0);;
    float diff= clamp(dot(N, L), 0.0, 1.0);
    vec3 diffuse =  lightColor * diff;
    float shininess=10.0;
    float specular    = pow(max(dot(R, V), 0.0), shininess);

    vec3 back = 0.5 * lightColor * clamp(dot(N, -L), 0.0, 1.0); // back
    vec3 colOut = occ*lightColor*(ambient+diffuse*shadow+.25 +back) + vec3(.7,.9,1)*specular*specular;

    // efecto
    if(2.0*sin(iTime)>0.0) 
    colOut=(colOut+ pallete(diffuse.x) * max(0.0, dot(N, L)))/2.0;  //-2-
    return colOut;
}

vec3 render_sky_color(vec3 rd)
{   float t = (rd.x + 1.0) / 2.0;
    vec3 col= vec3((1.0 - t) + t * 0.3, (1.0 - t) + t * 0.5, (1.0 - t) + t);
    vec3  sky = mix(vec3(.0, .1, .7)*col, vec3(.3, .8+0.6*sin(iTime), .8), 1.0 - rd.y);
    float d = pow(rd.z+0.3,2.)*pow(rd.z*0.5+0.5,1.)/pow(1.3,2.)*0.9+0.1;
	return sky*d;
}


vec3 GetMaterial(vec3 p,  vec3 nor, vec3 ro,  vec3 rd, int id_color)
{  	vec3 colobj; 
     if (id_color<40) { colobj=getColor(int( id_color));  return colobj; }

}

vec3 linear2srgb(vec3 c) 
{ return mix(12.92 * c,1.055 * pow(c, vec3(1.0/1.8)) - 0.055, step(vec3(0.0031308), c)); }

vec3 exposureToneMapping(float exposure, vec3 hdrColor) 
{ return vec3(1.0) - exp(-hdrColor * exposure); }

vec3 ACESFilm(vec3 x)
{   float a,b,c,d,e;
    a = 2.51; b = 0.03; c = 2.43;d = 0.59; e = 0.14;
    return (x*(a*x+b))/(x*(c*x+d)+e);
}

vec3 Render(vec3 ro, vec3 rd)
{  vec3 col = vec3(0);
  vec3 p;
     vec2 hit=RayMarch(ro,rd, MAX_STEPS);
      if(hit.x<MAX_DIST)
       {   p = (ro + rd * hit.x );
        vec3 nor=GetNormal(p);
        vec3 colobj;
        colobj=GetMaterial( p, nor, ro, rd,  int(hit.y));
        vec3 result;
         result= lightingv3(nor, p,light_pos1, rd,ro,colobj,hit.x)*light_color1;
        result+= lightingv3(nor, p,light_pos2,rd, ro,colobj,hit.x)*light_color2;
        col= result/2.0;
        col= (ACESFilm(col)+linear2srgb(col)+col+ exposureToneMapping(3.0, col))/4.0 ;


    }
    else if(hit.x>MAX_DIST)
    col=  ACESFilm(render_sky_color(rd));
   return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float t;
    t=mod(iTime*1.0,360.0);
 	light_pos1= vec3(-10.0, 120.0, -25.); light_color1=vec3( 1.0,1.0,1.0 );
 	light_pos2= vec3(10.0, 20.0, -25.0 ); light_color2 =vec3( 0.7 +0.5*sin(t)); 
   vec3 ro=vec3(0.0,0.0,-25.0);
   vec3 rd=normalize( vec3(uv.x,uv.y,1.0));
    light_pos1+=ro;
    light_pos2+=ro;
    vec3 col= Render( ro,  rd);
    fragColor = vec4(col,1.0);
}
