vec3 amigaTexture(vec2 pos, float n)
{
    float l = clamp(sin(pos.x*n)*sin(pos.y*n)*1000.0, 0.0, 1.0);
    return vec3(0.0, 0.0, 0.0)*l+(vec3(1.0, 0.0, 1.0)*(1.0-l));
}


