    vec3 ro=vec3(0.0,7.0,-25.0+t*1.2);
   ro.z=opRep1D(ro.z, 30.5*10.0 );
   ro.y+=1.5 * sin(ro.z * 0.2)+2.0;
   vec3 rd=normalize( vec3(uv.x,uv.y,1.0));


   float dc=6.0*sin(t0); //cambio de camara
   if( dc<0.0)
     {   ro+=vec3(-3.0,0.0,0.0);
         rd=rotate_y(rd, radians(90.0)); }

    light_pos1+=ro;
    light_pos2+=ro;
   vec3 col= Render( ro,  rd);
   fragColor = vec4(col,1.0);
}

