mat2 rot(float angle) {
	float s = sin(angle);
	float c = cos(angle);

	return mat2(
		c, -s,
		s, c
	);
}

vec2 rand(vec2 seed) {
    return (sin(dot(cos(seed * 12345.345), seed + 25.) * seed + 553.) + 1.) / 2.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.x;
    uv.y += sin(floor(uv.x * 10.) + iTime) * 0.1;
    vec2 gid = floor(uv * 10.);
    vec2 guv = (fract(uv * 10.) - 0.5) * 2.;
    guv = rot(sin(iTime * gid.y) * 3.1415) * guv;

     // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 col = vec3(0.);
    if (min(abs(guv.x), abs(guv.y)) < 0.1 * smoothstep(0.2, 1., abs(sin(iTime * 0.2 + uv.x * 2. + uv.y * 3.)))
            && max(abs(guv.x), abs(guv.y)) < 0.5)
            {
        col = vec3(1.) * (sin(iTime * 3. + 3.1415 * rand(gid).x) + 1.) / 2.;
        if (rand(gid).x < rand(gid).y) {
            col  *= 0.5 + 0.5*cos(gid.xyx+vec3(0,2,4));
        }
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}
