#ifdef GL_ES
precision highp float;
#endif
#define PI 3.14159265359
#define N 30.0

vec3 checker(in vec2 st)
{
  float checkSize = 2.0;
  float fmodResult = mod(floor(checkSize * st.x) + floor(checkSize * st.y), 2.0);
  return vec3(smoothstep(1.0, 0.0, sign(fmodResult)));
}


mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}
vec2 rotate(in vec2 inp)
{
    inp *=  1.+(.05*sin(iTime/(1.618*10.)));
    inp *= rotate2d(.15*cos(iTime/10.0));
    return inp;
}

vec3 pattern(vec2 st, vec3 color, float n)
{
    st += iResolution.xy;
    st *= n;      
    st = fract(st);
    return color * checker(st);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = ( fragCoord - .5* iResolution.y) / iResolution. y;
    vec3 color = vec3(1.0, 1.0, 1.0);
    vec3 pattern1 = pattern(st, color, N);
    vec2 st2 = rotate(st);

    vec3 pattern2 = pattern(st2, color, N);
    vec3 dots = pattern1  + pattern2;
	dots = 1.0-(dots*1.0); //inverse
    fragColor = vec4(dots,1.0);
}
