#define S(v,r) smoothstep( r, r+ 5./iResolution.y, v )


float hash(vec2 p){
    p = vec2( dot(p,vec2(137.1,373.7)), dot(p,vec2(269.5,183.7)) ); 
    float f = dot(p,vec2(82.291,19.283));
    return fract(sin(f)*43758.37); 
}
float logo(vec2 uv);

float dist(vec2 p,float t){
    p=abs(p);
    return pow(pow(p.x,t)+pow(p.y,t),1./t);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    vec2 uv1=uv*3. + vec2(iTime*0.4,0);
    vec2 gv = fract(uv1)-.5;//grid uv

    vec3 col;
    //if(gv.x>.48 ||gv.y>.48) col = vec3(1,0,.6);//grid line

    if(hash(floor(uv1))<0.5) gv.x*=-1.;//add random


    float t = clamp(sin(iTime),-.5,.5)+1.5;//1~2
    float w = t*-0.1+0.35;

    float m = 2.-smoothstep(w,w+15./iResolution.y,abs(dist(gv-0.5,t)-0.5))
                -smoothstep(w,w+15./iResolution.y,abs(dist(gv+0.5,t)-0.5));//patterrn
         // m=smoothstep(w,w+15./iResolution.y,abs(dist(gv+.5,t)-.5));

    //display
    m *= S(0.75,1.-abs(uv.y));
    col = mix(col,vec3(0,0,1),S(1.2,abs(uv.x))*S(0.6,abs(uv.y)));//bluebox
    col =mix(col,vec3(1),m+logo(uv));//white patterns

    //col= vec3(0);
    //col += smoothstep(0.,0.05,abs(dist(gv-0.5,1.)-0.5));


    fragColor = vec4(col,1.0);
}


float logo(vec2 uv){
    float n = S(.05,abs(length(uv-vec2(-1.23,0))-.12));
    n *= 1.-S(-1.25,uv.x);
    n =max(n, S(.17,abs(uv.y))*S(.05,abs(uv.x+1.29)));
    n += S(.05,abs(length(uv-vec2(-1.5,0))-.12));
    n += S(.05,abs(length(uv-vec2(-0.9,0))-.12));
    n += clamp((1.+S(-.6,uv.x)-S(.05,uv.y)),0.,1.)*S(.05,abs(length(uv-vec2(-0.6,.03))-.09));
    n += clamp((1.-S(-.6,uv.x)+S(-.05,uv.y)),0.,1.)*S(.05,abs(length(uv-vec2(-0.6,-.04))-.09));
    return n;
}
