void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float a = 0.1;

    float t = iTime;//mod(iTime, 2. * 3.14159);
    float b = 0.5 * (1. + cos(0.2 * t));   
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 1.5 * (fragCoord - 0.5 * iResolution.xy)/iResolution.y;
    float theta = atan(uv.y, uv.x);

    uv = vec2(1.6 * uv.x - 1.2 * uv.y * cos(4. * theta + t), 1.2 * uv.x * sin(3. * theta + t) + 1.6 * uv.y);

    //float theta = atan(uv.y, uv.x);

    float d = b * ( abs(uv.x) + abs(uv.y))
            + (1.-b) * ( max(abs(uv.x), abs(uv.y)));

    float r = 1. - smoothstep(cos(10. * d + 5. * theta + t)- sin(3. * d + 2. * theta + t), 0.25 + 0.1 * cos(2. * t), 0.5);
    r = 4. * r * (1.-r);

    t += a;

     float g = 1. - smoothstep(cos(10. * d + 5. * theta + t)- sin(3. * d + 2. * theta + t), 0.25 + 0.1 * cos(2. * t), 0.5);
    g = 4. * g * (1.-g);

    t -= 2. * a;

     float b2 = 1. - smoothstep(cos(10. * d + 5. * theta + t)- sin(3. * d + 2. * theta + t), 0.25 + 0.1 * cos(2. * t), 0.5);
    b2 = 4. * b2 * (1.-b2);



    fragColor = vec4(r,g,b2,1.) * 1.4 * (1.-d * d);
}
