//operacion de Union  por FabriceNeyret2
#define opU3(d1, d2) ( d1.x < d2.x ? d1 : d2 )

float sdSphere( vec3 p, float s )
	{ return length(p)-s;}
float sdBox( vec3 p, vec3 b )
	{ vec3 d = abs(p) - b;   return length(max(d,0.0))+ min(max(d.x,max(d.y,d.z)),0.0); }
float sdRoundBox( vec3 p, vec3 b, float r )
	{ vec3 q = abs(p) - b; return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r; }
float sdTorus( vec3 p, vec2 t )
	{ vec2 q = vec2(length(p.xz)-t.x,p.y);  return length(q)-t.y; }
float sdCylinderYZ( vec3 p, vec2 h )
	{ vec2 d = abs(vec2(length(p.yz),p.x)) - h; return min(max(d.x,d.y),0.0) + length(max(d,0.0)); }
float sdCylinderXZ( vec3 p, vec2 h )
	{ vec2 d = abs(vec2(length(p.xz),p.y)) - h; return min(max(d.x,d.y),0.0) + length(max(d,0.0)); }
float sdCylinderXY( vec3 p, vec2 h )
	{ vec2 d = abs(vec2(length(p.xy),p.z)) - h; return min(max(d.x,d.y),0.0) + length(max(d,0.0)); }

float opSmoothUnion( float d1, float d2, float k ) 
{   float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float opSmoothSubtraction( float d1, float d2, float k ) 
{   float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); }

float opSmoothIntersection( float d1, float d2, float k ) 
{   float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h); }

float sdEllipsoid( vec3 p, vec3 r )
{ float k0 = length(p/r);  float k1 = length(p/(r*r));  return k0*(k0-1.0)/k1;}

///----------Operacion de Distancia--------
float intersectSDF(float distA, float distB)
	{ return max(distA, distB);}
float unionSDF(float distA, float distB)
	{ return min(distA, distB);}
float differenceSDF(float distA, float distB)
	{ return max(distA, -distB);}

// object transformation
vec3 rotate_x(vec3 p, float phi)
{ float c = cos(phi);float s = sin(phi);return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);}
vec3 rotate_y(vec3 p, float phi)
{float c = cos(phi);float s = sin(phi);	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);}
vec3 rotate_z(vec3 p, float phi)
{float c = cos(phi);float s = sin(phi);	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);}
vec2 rotatev2(vec2 p, float ang)
{float c = cos(ang); float s = sin(ang); return vec2(p.x*c - p.y*s, p.x*s + p.y*c);}

vec3 GetDist(vec3 p  ) 
{	vec3 res= vec3(9999.0, -1.0,-1.0); 
    p.y=p.y-10.0;
    vec3 p0=p;

	float planeDist1 = p.y+10.0;  //piso inf
    res =opU3(res, vec3(planeDist1,-1.0,7.0)); //inf

     //cabeza 
    float dCabeza= sdEllipsoid( p- vec3(0.0,0.0,0.0), vec3(5.0,6.0,4.0) ) ;
    p.x=abs(p.x)-1.5;
    float dOjosHueco=sdEllipsoid(p-vec3(0.0,0.0,-2.5), vec3(1.5,1.5,1.5) );
    dCabeza= opSmoothSubtraction( dOjosHueco, dCabeza, 0.35) ;

    float t=iTime*2.0;
    vec3 pos=vec3(0.0+0.3*sin(t),0.3*cos(t),0.0);
    float  dIrisG=sdEllipsoid(p-vec3(0.0,0.0,-4.0)+pos, vec3(0.9,1.0,1.5) );
    float  dPupilaCh=sdEllipsoid(p-vec3(0.0,0.0,-4.0)+pos*0.8, vec3(0.5,0.7,1.5) );

    vec3 p2=p0;
    float  dPupilaRefA=sdEllipsoid(p2-vec3(-1.7+0.25,0.25,-4.0)-vec3(pos.x,-pos.y,pos.z)*0.5, vec3(0.25,0.27,1.5) );
    float  dPupilaRefB=sdEllipsoid(p2-vec3( 1.7-0.25,0.25,-4.0)+pos*0.5, vec3(0.25,0.27,1.5) );

    dIrisG= intersectSDF(dOjosHueco,dIrisG);
    dPupilaCh= intersectSDF(dOjosHueco,dPupilaCh);
    dPupilaRefA= intersectSDF(dOjosHueco,dPupilaRefA);
    dPupilaRefB= intersectSDF(dOjosHueco,dPupilaRefB);

    p=p0;
    float dBocaHueco=sdEllipsoid(p-vec3(0.0,-3.0,-3.0), vec3(1.5,0.8+0.3*sin(iTime),1.5) );
    float dBocaDif=sdEllipsoid(p-vec3(0.0,-3.0,-1.5), vec3(2.5,1.0,1.6) );
    float dBocaFondo=sdEllipsoid(p-vec3(0.0,-3.0,-1.0), vec3(2.5,1.0,1.5) );
    float dBocaDientesA=sdEllipsoid(p-vec3(-0.5,-2.3,-1.5), vec3(0.5,0.85,1.5) );
    float dBocaDientesB=sdEllipsoid(p-vec3( 0.0,-2.3,-1.5), vec3(0.5,0.95,1.5) );
    float dBocaDientesC=sdEllipsoid(p-vec3( 0.5,-2.4,-1.5), vec3(0.5,0.85,1.5) );

    dCabeza= opSmoothSubtraction( dBocaHueco,dCabeza, 0.35) ;
    dCabeza= differenceSDF(dCabeza,dBocaDif);

    vec3 prn=rotate_x(p-vec3(0.,-1.0,-2.7), radians(-30.0));
    float dNariz= sdRoundBox(prn, vec3(0.065,0.8,1.5), 0.25 );
    dCabeza= opSmoothUnion( dCabeza, dNariz, 0.25 ) ;
    res =opU3(res, vec3(dCabeza,33.0,-1.0));
    res =opU3(res, vec3(dOjosHueco,1.0,-1.0));
    res =opU3(res, vec3(dIrisG,-1.0,10.0));
    res =opU3(res, vec3(dPupilaCh,0.0,-1.0));
    res =opU3(res, vec3(dPupilaRefA,1.0,-1.0));
    res =opU3(res, vec3(dPupilaRefB,1.0,-1.0));
    res =opU3(res, vec3(dBocaFondo,0.0,-1.0));
    res =opU3(res, vec3(dBocaDientesA,1.0,-1.0));
    res =opU3(res, vec3(dBocaDientesB,1.0,-1.0));
    res =opU3(res, vec3(dBocaDientesC,1.0,-1.0));

    //pelo
    p=p0;
    float dpelo1=sdTorus( p.xzy-vec3(0.0,-2.5,-0.5), vec2(5.0,0.3) );
    vec3 pr1=rotate_y(p, radians(-10.0));
    float dpelo2=sdTorus( pr1.xzy-vec3(0.0,-1.5,-1.5), vec2(7.0,0.28) );
    vec3 pr2=rotate_y(p, radians(10.0));
    float dpelo3=sdTorus( pr2.xzy-vec3(0.0,-1.5,-1.5), vec2(7.0,0.28) );

    float dpelodif= sdBox( p- vec3(0.0,-5.5,0.0), vec3(8.0,3.0,3.0) );
    dpelo1= differenceSDF(dpelo1,dpelodif);
    dpelo2= differenceSDF(dpelo2,dpelodif);
    dpelo3= differenceSDF(dpelo3,dpelodif);
    res =opU3(res, vec3(dpelo1,2.0,-1.0));
    res =opU3(res, vec3(dpelo2,5.0,-1.0));
    res =opU3(res, vec3(dpelo3,6.0,-1.0));

    //ceja
    p.x=abs(p.x)-2.0;
    float dCeja= sdBox( p-vec3(0.0,2.0,-2.0), vec3(1.0,0.125,2.0) );
    dCeja= intersectSDF(dCeja,dCabeza);

    res =opU3(res, vec3(dCeja,0.0,-1.0));
    return res;
}

vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 nor = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    nor= normalize(nor);
    return nor;
}

float RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   float t = 0.; 
    vec3 dS=vec3(9999.0,-1.0,-1.0);
    float marchCount = 0.0;
    vec3 p;
    float minDist = 9999.0; 

    for(int i=0; i <= PMaxSteps; i++) 
    {  	p = ro + rd*t;
        dS = GetDist(p);
        if ( abs(dS.x)<MIN_DIST  || i == PMaxSteps)
        {mObj.hitbln = true; minDist = abs(t); break;}
        if(t>MAX_DIST)
            {mObj.hitbln = false;    minDist = t;    break; }         
        t += dS.x;
        marchCount++;
    }
    mObj.dist = minDist;
    mObj.id_color = dS.y;
    mObj.marchCount=marchCount;
    mObj.id_material=dS.z;
    mObj.normal=GetNormal(p);
    mObj.phit=p;
    return t;
}

float GetShadow(vec3 p, vec3 plig)
{   vec3 lightPos = plig;
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);
    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*MIN_DIST*2., l , MAX_STEPS/2);
    if(d<length(lightPos-p)) dif *= .1;
    return dif;
}

float FakeSoftShadow(float result, vec3 LightPos,vec3 point,vec3 normal)
{    vec3 shadowOffset = normal * 0.2 ;
    //float result = 0.2;
    int epoch = 5;
    float delta = result/float(epoch);
    float lightOffset = 0.2;

    for(int i = 0; i<epoch; i++)
    {
        vec3 tmp = LightPos +vec3(0,0,i)*lightOffset;
        float dis=RayMarch(point + shadowOffset,normalize(tmp-point), int( MAX_DIST));
        if (dis< length(tmp-point))
        {
            result -= delta;
        }
    }
    return result;
}

// util
#define saturate(x) clamp(x, 0.0, 1.0)
float calcAo(in vec3 p, in vec3 n)
{
    float k = 1.0, occ = 0.0;
    for(int i = 0; i < 5; i++){
        float len = 0.15 + float(i) * 0.15;
        float distancee = GetDist(n * len + p).x;
        occ += (len - distancee) * k;
        k *= 0.5;
    }
    return saturate(1.0 - occ);
}

