vec3 Shading(vec3 LightPos, vec3 pos, vec3 ro, vec3 baseColor)
{
    //ro=eye
    float roughness=0.5;
    float alpha = roughness*roughness;
    vec3 L = normalize(LightPos - pos);
    vec3 V = normalize(ro - pos);
    vec3 N = GetNormal(pos);
    vec3 H = normalize(L+V);
    vec3 R = normalize(reflect(-L, N));

    float dotNL = clamp (dot (N, L), 0.0, 1.0);
	float dotNV = clamp (dot (N, V), 0.0, 1.0);
	float dotNH = clamp (dot (N, H), 0.0, 1.0);
	float dotLH = clamp (dot (L, H), 0.0, 1.0);

    float reflectance=0.0125;
    float metalMask=0.23;
    vec3 F0 = 0.16*reflectance*reflectance * (1.0-metalMask) + baseColor*metalMask;

    float Ambient = 0.1;
    float D, vis=1.0;
    vec3 F;
    // Fresnel (Schlick)
	float dotLH5 = pow (1.0 - dotLH, 5.0);
	F = F0 + (1.0 - F0)*(dotLH5);

    // NDF : GGX
	float alphaSqr = alpha*alpha;
	float pi = 3.1415926535;
	float denom = dotNH * dotNH *(alphaSqr - 1.0) + 1.0;
	D = alphaSqr / (pi * denom * denom);

    float result = max(dotNL,0.0)*0.5 + pow(max(dotNH,0.0),20.)*0.9;
    result = FakeSoftShadow(result, LightPos,pos,N);

    vec3 specular =  D * F * vis;  
      return (result+Ambient)*baseColor+specular;    


}

