//Daily Shader Challenge Day 7
//Experimenting with more 2D shaders ( Now with mouse support)
//tried going for that 70s font
//Lopea (https://github.com/lopea)


float line(vec2 u, vec2 l)
{
    vec2 p = abs(u) - l;

    return length(max(p, 0.)) + min(max(p.x,p.y), 0.);
}
mat2 rot(float a )
{
    float ca = cos(a), sa = sin(a);
    return mat2(ca, -sa, sa, ca);
}
float smoothun(float d1 ,float d2, float k )
{
    float h = clamp(.5+ .5*(d2-d1)/k,0., 1.);
    return mix(d2, d1, h) - k*h*(1.-h);
}
float sev(vec2 x, float tt)
{
    vec2 sx = x;
    sx.x = sx.x - sx.y*atan(radians(35.));
    float res = (min(line(sx,vec2(.1,.24)), line(sx + vec2(.15,-0.35),vec2(.25,.1))))-.02;


    res = max(mod(res, atan(tt) * .02),res);

    return res;
}


vec4 gc(vec2 u, float a, float tt)
{
    float rr = (step(sev(u - vec2(0.,-.15) + vec2(.004)*a, tt), 0.01));
        float gg = (step(sev(u - vec2(0.,-.15) -vec2(.0004)*a,tt), 0.01));
        float bb = (step(sev(u - vec2(0.,-.15) - vec2(.004)*a,tt), 0.01));

    return vec4(rr,gg,bb, 1);
}

void mainImage(out vec4 c, in vec2 f)
{
    vec2 r = iResolution.xy, u = (2.* f -r)/r.y;
    float tt = iTime;
    if(iFrame < 5)
    {
        tt += 10.;
    }


    u *= .65;
    float a = max(atan (tt - 6.5),0.);
    float b = atan(smoothstep (0.,0.1, a * 0.1));
    vec2 m = (2.* iMouse.xy - r)/r.y; 
    u += m*.01;
    if(b != 0.)
    {
        u.x = mod(u.x + b/2., b) -b/2.;


    for(int i = 0 ; i < 7; i ++)
    {
        c += gc(u,a,tt);
        u *=.99 * a;
        u += m*.05;
    }
        c/=2.;
    }
    else
    {
        c = gc(u,a,tt);
    }
}
