#define PI                3.141592654

/*
Egg shape

Equation from this paper: https://nyjp07.com/index_egg_E.html
*/
float egg(vec2 uv, float a, float b_ratio) {
	float feather = .005;
	float x = uv.x;

	if (x < 0.) return 0.;
	if (x > a) return 0.;

	float b = a * b_ratio;
	float c = a - b;

	float up = sqrt(c - 2. * x + sqrt(4. * b * x + pow(c, 2.)));
	float y = up / sqrt(2.) * sqrt(x);

	return 1. - (smoothstep(y - feather, y + feather, uv.y) + smoothstep(y - feather, y + feather, -uv.y));
}

vec2 rotate2D(vec2 uv, float angle) {
	uv -= .5;
	uv = mat2(vec2(cos(angle), -sin(angle)), vec2(sin(angle), cos(angle))) * uv;
	uv += .5;
	return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = -1.0 + 2.0*uv;
    p.x *= iResolution.x/iResolution.y;

    // Position and rotation
    p = rotate2D(p, PI * -.5);
    p.x -= .5;

    // Make egg shape
    vec3 egg_shape = vec3(egg(p, 1., .7));

    // Output to screen
    fragColor = vec4(egg_shape,1.0);
}
