// Implemented from https://wikipedia.org/wiki/Julia_set#Pseudocode_for_normal_Julia_sets
// uv.y goes from -1 to 1, so R is always 2.

#define MAX_ITER 200.0

float julia(vec2 uv, vec2 c) {
    float iter = 0.0;

    float perturbation = sin(iTime) / 25.;
    for(;
        dot(uv, uv) < 4.0 
        && iter < MAX_ITER;
        iter++) {
        uv = vec2(uv.x * uv.x - uv.y * uv.y, 2.0 * uv.x * uv.y) + c + vec2(perturbation);
    }    
    return iter;
}

vec3 color_julia(float iter) {
    float c = (MAX_ITER - iter < 0.0) ? 1.0
                                      : iter / MAX_ITER;
    return smoothstep(0.0, 1.0, 2.0 * c  - vec3(2, 0, 0));                       
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // uv.y in [-1, 1] and uv.x in [- aspect-ratio, aspect-ratio]
    vec2 uv = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.y;

    uv *= .5;                           // zoom in by 2x
    uv *= abs(sin(iTime * 0.6)) + .07;  // adjust zoom
    uv -= vec2(.212094, .403085);       // offset center

    const vec2 c = vec2(-0.70176, -0.3842);  // copied off wiki
    float iter = julia(uv, c);
    vec3 col = color_julia(iter);

    //vec3 col = uv.xyx;
    fragColor = vec4(col,1.0);
}
