vec3 WHITE = vec3(1);
vec3 BLACK = vec3(0);
vec3 SAFFRON = vec3(255,153,51)/255.;
vec3 GREEN = vec3(19,136,8)/255.;
vec3 BLUE = vec3(0,0,128)/255.;
float PI = radians(180.);


float get_band(float x, float width,  float smoothing_d){
return smoothstep(0., smoothing_d, x)*smoothstep(0., smoothing_d, -x + width);

}

vec3 Flag(vec2 uv, float ratio){

// Normalized pixel coordinates (from 0 to 1)



    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));


    float band = floor(uv.y*3.);

    col = BLACK;

    //float flag_region = smoothstep(0., 0.01, band+1.)*smoothstep(0., 0.01, -band + 3.);
    float flag_region = get_band(band+1., 4., 0.001);

    float top = flag_region*smoothstep(0., 0.01, band + 3.);
    col = mix(col, GREEN, top);

    top = top*smoothstep(0., 0.01, band);
    col = mix(col, WHITE, top);

    top = top*smoothstep(0., 0.01, band-1.);
    col = mix(col, SAFFRON, top);


    /**
    if(band < 3. && band > -1.){
        if (band > 1.){
            col = SAFFRON;
        } else if (band > 0.) {
            col = WHITE;
        } else if (band > -1.){
            col = GREEN; 
        }
    }
    **/


    vec2 center = vec2(0.5);
    vec2 cuv = uv - center;
    cuv.y = cuv.y*ratio;
    float d = length(cuv);
    //float angle = (PI + atan(cuv.y, cuv.x))/2.*PI;
    float angle = atan(cuv.y, cuv.x)/PI;
    angle = 12.*angle;
    angle = fract(angle);

    float circle_r = 0.09;
    float circle_w = 0.01;

    //float circle_rim = smoothstep(0., 0.01, d-circle_r + circle_w)*smoothstep(0., 0.01, d-circle_r);
    float circle_rim = get_band(d-(circle_r - circle_w), circle_w, 0.001);
    col = mix(col, BLUE, circle_rim);

    /**
    if(d > circle_r - circle_w && d < circle_r){
        col = BLUE;
    }
    **/

    col = mix(col, BLUE, smoothstep(0., 0.001, -d+0.01));
    /**
    if(d < 0.01){
        col = BLUE;
    }
    **/


    col = mix(col, BLUE, smoothstep(0., 0.001, -d+circle_r)*get_band(angle-0.8, 0.2, 0.01));

    /**
    if(d < circle_r && angle > 0.8){
        col = BLUE;
    }
    **/

    col = mix(BLACK, col, flag_region);

    return col;


}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec2 uv = fragCoord/iResolution.xy;

    float t = uv.x*7. - 2.*iTime + uv.y*3.;

    uv.y += sin(t)*0.03;

    float ratio = iResolution.y/iResolution.x;
    vec3 col = Flag(uv, ratio);

    col *= .9 + cos(t)*0.2;
    // Output to screen
    fragColor = vec4(col,1.0);
}
