// sigmoid function (not in use)
float sig(float x) {
    return x / (1. + abs(x));
}

float h21 (vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ) {
    return a + b*cos( 6.28318*(c*t+d) );
}

// smooth "square" wave, tends to cos() as a->0
float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;
    // uv.y += 0.02 * cos(40. * uv.x + iTime);

    float k = 2. * thc(4., 0.5 * iTime);
    float time = thc(4., k * uv.x + iTime) + iTime;

    // vertical wobble
    uv.y += 0.025 * thc(0.5, 16. * uv.x + 2. * time);

    // only used for vertical sections (I think?)
    float rand = h21(vec2(floor(100. * uv.y))); // 20. * uv.y

    // position of blob
    float f = 0.5 * thc(4., 0.5 * rand + time); // 10. * rand
    vec2 p = vec2(f, 0.);

    // distance from blob + noise
    float d = length(uv-p) - 0.1 + 0.08 * thc(1.5, 100. * pow(h21(uv),4.) + 3. * time);
    float s = step(d, 0.1 + 0.025 * thc(4., 0.4 * rand - 2. * time));

    vec3 col = s * pal(f + 0.1 * iTime, vec3(0.5), vec3(0.5), vec3(1.5), vec3(0.,0.333,0.666));

    fragColor = vec4(col,1.0);
}
