vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = texelFetch( iChannel0, ivec2(0.4 * fragCoord), 0 ).rgb;
    col += 0.5;
    col *= 2.5 * col * col;
    vec3 e = vec3(0.5);
    vec3 col2 = pal(0.75 + 0.5 * length(col), col,  min(col,1.-col), e, 2.5 * col);
    vec3 col3 = pal(-0.25 + 0.5 * length(col), col, min(col,1.-col), e, 0.6 * vec3(1));
    col = mix(col, col2, step(0. * iResolution.x, fragCoord.x));
    //col = mix(col, col3, step(fragCoord.x, 0.5 * iResolution.x));

    fragColor.rgb = vec3(col);
}

#define KEYBOARD iChannel1
#define KEY_RESET 82


vec3 Cell( in ivec2 p )
{
    // do wrapping
    ivec2 r = ivec2(textureSize(iChannel0, 0));
    p = (p+r) % r;

    // fetch texel
   // return (texelFetch(iChannel0, p, 0 ).x > 0.5 ) ? 1 : 0;
   return texelFetch(iChannel0, p, 0 ).rgb;
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

float h21(vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

bool key_down(int key) {
    return int(texelFetch(KEYBOARD, ivec2(key, 0), 0).x) == 1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 px = ivec2( fragCoord );

    if (iFrame == 0 || key_down(KEY_RESET)) {
        vec2 f = fragCoord + 0.01 * iTime; 
        float o = 0.01;
        vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
        float s = step(abs(length(uv)-0.3), 0.005);
        fragColor.rgb = (vec3(h21(f-o), h21(f), h21(f+o)) - 0.5);
        return;
    }

    // center cell
    vec3 e = Cell(px); 

    // neighbour cells
    vec3 t = Cell(px + ivec2(0,-1));
    vec3 b = Cell(px + ivec2(0,1));
    vec3 l = Cell(px + ivec2(-1,0));
    vec3 r = Cell(px + ivec2(1,0));   

    vec3 h = 0.5 * (l+r);
    vec3 v = 0.5 * (t + b);  
    vec3 k = max(h,v); // "average" of neighbours
   // vec3 k = 0.5 *(h+v);
    k = 0.25 * (l + r + t + b);

    // difference between center and average
    vec3 j = e - k;


    float a = 0.;
    // try length(e), length(k), length(1.5 * j)
    vec3 le = ((1.-a) + a * cos(3.14159 * e)) * length(e);//(length(e) > 0.5) ? length(e) : length(k);
    vec3 se = sign(e);

    // e.g. 2., 2.5, higher values have less effect
    // try changing sc with "k = erot(...)" commented out
    float sc = 32.;

    e = erot(e, normalize(k), 3.14159/sc);
    k = erot(k, normalize(e), 3.14159/(1.5*sc) + 0. *(j.x+j.y+j.z));

    // most important part. se lets it grow
    // le * normalize() so e stays in -0.5,0.5 sphere
    e = -0.005 * se + le * mix(normalize(k), normalize(e), 0.5 * normalize(j));

    //e = fract((e+0.4975)) - 0.5;
    e = clamp(e,-0.5, 0.5);

    // weird box patterns
    //e *= 100.;

	fragColor = vec4( e, 0.0 );
}

// Flicker reduction.
// This CA tends to produce structures that flicker between two states.
// Code here copies CA output on odd frames and stays unchanged on even.
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    if (iFrame % 2 == 1)
        fragColor.rgb = texelFetch(iChannel0, ivec2(fragCoord), 0).rgb;
    else
        fragColor.rgb = texelFetch(iChannel1, ivec2(fragCoord), 0).rgb;
}
