const float gridSize = 75.;
const float sphereSize = 0.92;
const float speed = 10.;

//https://www.shadertoy.com/view/4djSRW
vec3 hash32(vec2 p) {
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

//https://www.shadertoy.com/view/lsS3Wc
vec3 hsl2rgb( in vec3 c ) {
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec3 lightDir = normalize(vec3(cos(iTime)*0.25,0.5,sin(iTime)*0.25));

    //slowly panning coordinates
    vec2 uv = (fragCoord+iTime*vec2(2.,1.)*speed)/gridSize;

    //a square that contains the sphere
    vec2 uv2 = (fract(uv)*2.-1.)/sphereSize;

    // a method to get sphere normals in orthographic projection
    // (if d >= 1. then we are not touching the sphere)  
    float d = dot(uv2,uv2);
    float z = (d>=1.) ? 0. : sqrt(1.-d);
    vec3 normals = vec3(uv2,z);

    //phong reflection model
    float specular = pow(max(2.*z*dot(lightDir,normals) -lightDir.z, 0.0),20.)*0.25;
    float diffuse = 0.5 + dot(normals,lightDir)*0.5;

    vec2 seed = floor(uv);
    vec3 rnd = hash32(seed);

    //we create 2 slightly different colors

    float hue  = rnd.x;
    float hue2 = rnd.x + 0.25 + fract(rnd.x*99.99)*0.1;

    float saturation = 0.1 + rnd.z*0.9;

    float luminosity  = 0.05 + rnd.y*0.5;
    float luminosity2 = 0.2  + rnd.y*0.5;

    vec3 colA = hsl2rgb(vec3(hue , saturation, luminosity));
    vec3 colB = hsl2rgb(vec3(hue2, saturation, luminosity2));

    vec3 col = mix(colA, colB, diffuse);

    //light space blending
    //vec3 col = pow(mix(pow(colA,vec3(2.2)), pow(colB,vec3(2.2)), diffuse),vec3(1./2.2));

    //antialiasing
    col *= min(15.*z*z,1.);

    // Output to screen
    fragColor = vec4(col+specular,1.0);
}
