float brick(vec2 p)
{
    // offset for each row
    float offset = sqrt(floor(mod(p.y,2.)+16.)*16.);
    p.x += offset;

    vec2 m = floor(p);
    vec2 n = fract(p)*2.-1.;

    vec2 nn = pow(n, vec2(10.));
    float d = pow(nn.x+nn.y, .1)-0.07; // controls size of the brick

    // tune softness
    //return pow(smoothstep(1., .8,d),5.);
    return pow(smoothstep(1., .8,d),1.);
}

vec3 shading(vec3 n)
{
    vec3 L = normalize(vec3(-.7,.8,1.));
    float dif = max(0., dot(L, n));
    return vec3(dif);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv *= vec2(4.*iResolution.x/iResolution.y,6.);
    uv.xy += iTime;

    vec2 eps=vec2(.012,0);
    vec3 nor = normalize(vec3(brick(uv-eps.xy) - brick(uv+eps.xy),
                              brick(uv-eps.yx) - brick(uv+eps.yx),1.));

    vec3 col = shading(nor);
    if(fragCoord.x > iResolution.x*.5) col = vec3(nor*.5+.5);

    fragColor = vec4(col,1.0);
}
