    O = vec4(0);
    for(float i; i<2e2; i+=1.39) {
        vec2 s =  i*vec2(sin(-i),cos(i));  // point sampling
        //vec2 s =  i*sin(i+vec2(0,11));
        vec4 C = texture(iChannel0, (I + d*s)/r + iTime*.01);
        O = max(O,C);  // depth layering
