vec3 balls[] = vec3[] (
    vec3(128, 128, 64),
    vec3(192, 256, 48),
    vec3(512, 384, 48),
    vec3(384, 448, 96)
);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float val = 0.0;

    for(int ix = 0; balls.length() - ix != 0; ++ ix) {
        vec3 ball = balls[ix];

        float ball_xd = (fragCoord.x - ball.x) * (fragCoord.x - ball.x);
        float ball_yd = (fragCoord.y - ball.y) * (fragCoord.y - ball.y);
        float ball_zd = ball.z * ball.z;

        val += ball_zd / (ball_xd + ball_yd);
    }

    float mouse_xd = (fragCoord.x - iMouse.x) * (fragCoord.x - iMouse.x);
    float mouse_yd = (fragCoord.y - iMouse.y) * (fragCoord.y - iMouse.y);
    float mouse_zd = 72.0 * 72.0;

    val += mouse_zd / (mouse_xd + mouse_yd);

    // float col = 1.0 / val > 1.0 ? 1.0 : 0.0;
    float col = 1.0 - smoothstep(0.98, 1.02, val); // Thanks, FabriceNeyret2, for mentioning smoothstep

    fragColor = vec4(col,col,col,1.0);
}
