////////////////////////////////////////////////////////////////////////////////
//
// Cos Accuracy II, mla 2022.
//
////////////////////////////////////////////////////////////////////////////////
//
// Trig function errors. This is mainly intended to show what happens on Intel
// where cos and sin are computed with a simple piecewise linear
// approximation, though there is a check for GPUs with better trig functions,
// eg. Nvidia, and zooms in vertically if so (it's just checking a single
// value so might give false positives though).

// Red channel: absolute error in cos(x)
// Green channel: absolute error in sin(x)
// Blue channel: ulps error in cos(x)
//
// Numeric display shows the main trig functions evaluated at the mouse position
// and compared with the more accurate myxxx functions. We also compute the inverse
// functions for comparison with the original theta.
//
//         mycos(theta)       mysin(theta)       mytan(theta)
//         cos(theta)         sin(theta)         tan(theta)
// theta   acos(mycos(theta)) asin(mysin(theta)) atan(mytan(theta))
//
////////////////////////////////////////////////////////////////////////////////

// mycos and mysin have been checked on CPU & agree to a few ulps with
// builtin functions in range [0..pi/2] (ulp error is larger when result is
// closer to zero, but absolute error is fine). It's possibly worth using
// sin(pi/2-x) for cos(x) in upper half of range, but there isn't a lot in it.

//#define CRENSHAW

float mycos0(float x, int N) {
  N *= 2;
  float x2 = x*x, y = 1.0;
  for (int n = N; n > 0; n-=2) {
    y = 1.0-x2*y/float(n*(n-1));
  }
  return y;
}

float mycos(float x) { return mycos0(x,8); }

// This is a pretty good Chebyshev approximaton to cos(x)
float mycos1(float x) {
  assert(x >= -1.5707963 && x <= 1.5707963);
  float x2 = x*x;
  float t = -0.0012711104;
  t = x2*t + 0.041487381;
  t = x2*t + -0.4999121;
  t = x2*t + 0.99999325;
  return t;
}

float mysin1(float x) {
  assert(x >= -1.5707963 && x <= 1.5707963);
  float x2 = x*x;
  float t = -0.00018362749;
  t = x2*t + 0.0083062861;
  t = x2*t + -0.16664824;
  t = x2*t + 0.9999966;
  t = x*t;
  return t;
}

float mysin(float x) {
  int N = 10;
  float x2 = x*x, y = 1.0;
  for (int n = N; n > 0; n-=2) {
    y = 1.0-x2*y/float(n*(n+1));
  }
  return x*y;
}

// From https://www.shadertoy.com/view/md3cDM by Envy24
// Source: "Math Toolkit For Real-Time Development", Jack W. Crenshaw
vec2 sincos(float x)
{
    const float sin_30 =      0.5;
    const float cos_30 =      0.8660254037844386;
    const float pi_over_six = 0.5235987755982988;    // pi / 6
    const float inv =         1.9098593171027440292; // 1 / pi_over_six
    const float tau =         6.2831853071795862;
    const float inv_tau =     0.1591549430918953;

    // Increases accuracy for negative angles (see this shader: https://www.shadertoy.com/view/NtVBWG)
    float X = fract(x*inv_tau);
          x = X*tau;

    int n = int(x * inv + 0.5); 
    x -= float(n) * pi_over_six;

    n = n < 0 ? (n + 12) % 12 : n % 12;
    //n %= 12; if (n < 0) n += 12;

    float z = x*x, s = 0., c = 0.,
          s1 = ((z/20.0-1.0)*z/6.0+1.0)*x,
          c1 = ((z/30.0+1.0)*z/12.0-1.0)*z/2.0+1.0;        

    switch(n)
    {
    case 0:  s =  s1;                        c =  c1;                        break;
    case 1:  s =  cos_30 * s1 + sin_30 * c1; c = -sin_30 * s1 + cos_30 * c1; break;
    case 2:  s =  sin_30 * s1 + cos_30 * c1; c = -cos_30 * s1 + sin_30 * c1; break;
    case 3:  s =  c1;                        c = -s1;                        break;
    case 4:  s = -sin_30 * s1 + cos_30 * c1; c = -cos_30 * s1 - sin_30 * c1; break;
    case 5:  s = -cos_30 * s1 + sin_30 * c1; c = -sin_30 * s1 - cos_30 * c1; break;
    case 6:  s = -s1;                        c = -c1;                        break;
    case 7:  s = -cos_30 * s1 - sin_30 * c1; c =  sin_30 * s1 - cos_30 * c1; break;
    case 8:  s = -sin_30 * s1 - cos_30 * c1; c =  cos_30 * s1 - sin_30 * c1; break;
    case 9:  s = -c1;                        c =  s1;                        break;
    case 10: s =  sin_30 * s1 - cos_30 * c1; c =  cos_30 * s1 + sin_30 * c1; break;
    case 11: s =  cos_30 * s1 - sin_30 * c1; c =  sin_30 * s1 + cos_30 * c1; break;
    }

    return vec2(s, c);
}

// Chebyshev approximation tailored for ±45° interval
float mysin2(float x) {
  assert(x >= -0.78539816 && x <= 0.78539816);
  float x2 = x*x;
  float t = 0.0081214934;
  t = x2*t + -0.16660157;
  t = x2*t + 0.99999499;
  t = x*t;
  return t;
}

float mycos2(float x) {
  assert(x >= -0.78539816 && x <= 0.78539816);
  float x2 = x*x;
  float  t = -0.0013585844;
  t = x2*t + 0.041655021;
  t = x2*t + -0.49999857;
  t = x2*t + 0.99999997;
  return t;
}

//#define cos(x) mycos2(x)
//#define sin(x) mysin2(x)
#if defined CRENSHAW
#define cos(x) sincos(x)[1]
#define sin(x) sincos(x)[0]
#endif

float hud(vec2 fragCoord, float theta) {
  const int digits = 7;
  const int nrows = 15, ncols = 4;
  // Table structure
  vec2 cellsize = iResolution.xy/vec2(ncols,nrows);
  ivec2 cellindex = ivec2(fragCoord/cellsize);
  float fsize = cellsize.y; // "Font size" = line height in pixels
  int column = cellindex.x;
  int row = cellindex.y;
  vec2 p = mod(fragCoord,cellsize)/fsize;
  float x;
  float cost = cos(theta);
  float mycost = mycos(theta);
  float sint = sin(theta);
  float mysint = mysin(theta);
  float mytant = mysint/mycost;
  if (column == 0) {
    if (row == 0) x = theta;
    else return 0.0;
  } else if (column == 1) {
    if (row == 1) x = cost;
    else if (row == 2) x = mycost;
    else if (row == 0) x = acos(mycost);
    else return 0.0;
  } else if (column == 2) {
    if (row == 1) x = sint;
    else if (row == 2) x = mysint;
    else if (row == 0) x = asin(mysint);
    else return 0.0;
  } else if (column == 3) {
    if (row == 1) x = tan(theta);
    else if (row == 2) x = mytant;
    else if (row == 0) x = atan(mytant);
    else return 0.0;
  } else {
    return 0.0;
  }

  // Draw the character
  vec4 metrics = vec4(0.25,0.75,0,1);
  float charwidth = metrics.y-metrics.x;
  float colwidth = cellsize.x/fsize-1.0; // Cell width in chars, with padding
  int charpos = int(p.x/charwidth);
  vec2 uv = vec2(0);
  int c = floatchar(x,digits,charpos); // Get character at charpos, 0 if out of range
  if (c < 0) return 0.0;
  float charstart = charwidth*float(charpos); // character start
  uv = char(vec2(p.x-charstart+metrics.x,p.y),c);
  float uvscale = 0.5/(16.0*fsize);   // 0.5 to sharpen a little
  vec2 dx = vec2(uvscale,0), dy = vec2(0,uvscale);
  vec4 t = textureGrad(iChannel0,uv,dx,dy);
  return t.x;  
}

bool comp(float y, float r) { return (0.0 < y) == (y < r); }

void mainImage(out vec4 fragColor, vec2 fragCoord ) {
  float scale = 1e-4;
  float check = max(0.75,-iTime);
  if (abs(cos(check)-mycos(check)) < 1e-6) scale = 1e-6;
  float x = 0.5*PI*(2.0*fragCoord.x-iResolution.x)/iResolution.x;
  float y = scale * (2.0*fragCoord.y-iResolution.y)/iResolution.y;
  float r = cos(x) - mycos(x);
  float s = sin(x) - mysin(x);
  // The ulps check assumes both values have the same sign.
  int ulps = floatBitsToInt(cos(x))-floatBitsToInt(mycos(x));
  vec3 col = vec3(comp(y,r),comp(y,s),comp(y,float(ulps)/float(1<<23)));

  float xmouse = 0.0;
  if (iMouse.x > 0.0) xmouse = 0.5*PI*(2.0*iMouse.x-iResolution.x)/iResolution.x;
  col = mix(col,vec3(1),hud(fragCoord,xmouse)); 
  if (alert) col.r = 1.0;  
  fragColor = vec4(col,1);
}

float PI = 3.141592654;

bool alert = false;
void assert(bool b) {
  if (!b) alert = true;
}

// https://www.shadertoy.com/view/4sBfRd
vec2 char(vec2 p, int c) {
  // Compute uv for font texture.
  // p is [0,1] coordinates in square character
  return (p + vec2(c,15-c/16))/16.0;
}

// Wrapper for division to allow emulation of GPU style division
// on CPU, not that this makes much difference to result.
float fdiv(float x, float y) { return x/y; }

// If we've found the character for the target index, return it.
#define CHAR(c) { if (index++ == cindex) return(c); }
#define SIGN(x) { if ((x) < 0.0) CHAR(MINUS) else CHAR(SPACE) } 

// Format x to digits precision and return the character at cindex,
// or -1 if cindex is out of range.
int floatchar(float x, int digits, int cindex) {
  if (cindex < 0 || cindex > digits+6) return -1;
  int index = 0;
  const float pmax = 1e15; //  Large power of 10 with a nice representation
  const int emax = 15; // Exponent of pmax
  const float pow10[] = float[]( 1e1,1e2,1e3,1e4,1e5,1e6,1e7,1e8 );
  const int SPACE = 0x20, ZERO = 0x30, POINT = 0x2e, MINUS = 0x2d, PLUS = 0x2b;
  const int A = 0x61, E = 0x65, F = 0x66, I = 0x69, N = 0x6e;
  if (isinf(x)) {
    SIGN(x);
    CHAR(I); CHAR(N); CHAR(F);
  } else if (isnan(x)) {
    // Maybe show the bits
    if (floatBitsToInt(x) < 0) CHAR(MINUS);
    CHAR(N); CHAR(A); CHAR(N);
  } else if (x == 0.0) {
    SIGN(x);
    CHAR(ZERO);
  } else {
    SIGN(x);
    x = abs(x);
    int exp = 0; // Compute exponent here
    float llimit = pow10[digits-2], ulimit = 10.0*llimit;
    // Now put x in the range llimit <= x <= ulimit
    // This is a delicate business if we aren't to
    // lose too much precision
    while (x > llimit*pmax) { x /= pmax; exp += emax; }
    while (x < fdiv(ulimit,pmax)) { x *= pmax; exp -= emax; }
    if (x >= ulimit) {
      float p = 1.0;
      while (fdiv(x,p) > ulimit) {
        exp++; p *= 10.0;
      }
      x = fdiv(x,p);
    } else if (x < llimit) {
      float p = 1.0;
      while (x*p < llimit) {
        exp--;  p *= 10.0;
      }
      x *= p;
    }
    assert(!isinf(x));
    assert(!isnan(x));
    assert(x >= llimit);
    assert(x <= ulimit); // Equality is possible!
    // Round to an integer which has exactly the significant figures we want.
    int n = int(round(x));
    if (n == int(ulimit)) {
      // Just in case the round has taken us the wrong way
      n /= 10; exp++;
    }
    assert(n >= int(llimit));
    assert(n < int(ulimit));
    // Now we have a correctly rounded integer representation together
    // with the correct exponent. The rest is just formatting.

    // See if we want to use exponent notation
    int e = 0;
    if (exp <= -digits-4) {
      e = exp+digits-1;
      exp = -digits+1;
    } else if (exp > 0) {
      e = exp+digits-1;
      exp = -digits+1;
    }
    while(n%10 == 0 && exp < 0) {
      // Remove trailing zeros
      n /= 10; exp++;
    }
    int m = 1, i = exp+1;
    while (10*m <= n) { m *= 10; i++; }
    // i is number of characters in front of decimal point
    if (i <= 0) {
      CHAR(ZERO);
      CHAR(POINT);
      // Add leading digits for 0 < x < 1
      while(i < 0) {
        CHAR(ZERO); i++;
      }
    }
    while(m != 0) {
      CHAR(ZERO+n/m%10);
      m /= 10; i--;
      // Don't print trailing point!
      if (i == 0 && m != 0) CHAR(POINT);
    }
    if (e != 0) {
      CHAR(E);
      CHAR(e < 0 ? MINUS : PLUS);
      e = abs(e);
      CHAR(ZERO+e/10); CHAR(ZERO+e%10); 
    }
  }
  return -1;
}
