float f_n_limited(
    float n_min, 
    float n_max, 
    float n
){
    return 
        max(min(n_min,n_max), min(max(n_min, n_max),n));
}
float f_n_limited2(
    float n_min, 
    float n_max, 
    float n
){
    float n_tmp = n_min;
    n_min = min(n_min,n_max);
    n_max = max(n_max, n_tmp);
    if(n < n_min) return 0.;
    if(n < n_max) return n ;
    if(n > n_max) return 1.;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 o_trpino = (fragCoord.xy - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_trpino_nooffset = (fragCoord.xy )/ iResolution.y;
    vec2 o_trmono = (iMouse.xy - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_trmono_nooffset = (iMouse.xy )/ iResolution.y;


    vec2 o_scale = vec2(0.01);
    float n_divisions = 10.;
    float n_scale = floor(o_trpino_nooffset.x*n_divisions)/n_divisions;
    //n_scale = n_scale * 0.1;
    //o_scale = vec2(0.01+n_scale);
    if(iMouse.z == 0.0){
        o_trmono_nooffset = vec2(sin(iTime), cos(iTime*0.2)*0.2+0.2);
    }
    o_scale= vec2(o_trmono_nooffset.y);
    if(o_trpino.x > o_trmono.x){
        o_scale= vec2(0.1);
    }
    vec2 o_fragCoord_scaled = ((fragCoord.xy)*o_scale);
    vec2 o_iResolution_scaled = (iResolution.xy * o_scale);

    vec2 o_fragCoord_scaled_floor = floor(o_fragCoord_scaled);
    vec2 o_iResolution_scaled_floor = floor(o_iResolution_scaled);
    vec2 o_fragCoord_scaled_fract = fract(o_fragCoord_scaled);
    vec2 o_iResolution_scaled_fract = fract(o_iResolution_scaled);



    vec2 o_trpino_scldfloor = (o_fragCoord_scaled_floor.xy - o_iResolution_scaled_floor.xy*0.5)/ o_iResolution_scaled_floor.y;
    vec2 o_trpino_nooffset_scaledfloor = (o_fragCoord_scaled_floor.xy)/ o_iResolution_scaled_floor.xy;



    float n_index = floor(o_fragCoord_scaled_floor.y * o_iResolution_scaled_floor.x + o_fragCoord_scaled_floor.x);
    float n_index_nor = n_index / (o_iResolution_scaled_floor.x * o_iResolution_scaled_floor.y);

    vec4 o_col = texture(iChannel0, o_trpino_nooffset_scaledfloor);
    float n_radius = length(vec2(0.5)-o_fragCoord_scaled_fract);
    n_radius *=2.;
    n_radius = pow(n_radius,5.);
    float b_ymod2 = mod(o_fragCoord_scaled_floor.y, 2.);
    float b_xmod2 = mod(o_fragCoord_scaled_floor.x, 2.);

    float n_r = float(b_ymod2 == 0. && b_xmod2 == 0.);
    float n_g = float(b_ymod2 == 0. && b_xmod2 == 1. || b_ymod2 == 1. && b_xmod2 == 0.);
    float n_b = float(b_ymod2 == 1. && b_xmod2 == 1.);


    //n_radius = f_n_limited2(0.4, 0.8, n_radius);
    vec4 o_col_mixed = mix(o_col, vec4(0.), n_radius);
    fragColor = o_col*vec4(
        n_r, 
        n_g/2.,
        n_b,
        1.
    );
    fragColor = sqrt(fragColor);
    //fragColor = vec4(n_radius);


}
